            }
            else {

                setListaIncidencias(getListaIncidenciasTotal.filter(
                    suggestion => ((suggestion.vigencia
                        + suggestion.created_at + suggestion.radicado +
                        suggestion.nombre_tipo_proceso + suggestion.descripcion
                        + suggestion.nombre_completo + suggestion.nombre_dependencia
                    ).toLowerCase().includes(event.target.value.toLowerCase()))
