import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import '../Constants';
import GenericApi from '../../Api/Services/GenericApi';

function ModalCierreEtapaInfo2(props) {
    
    const navigate = useNavigate();
    const [show, setShow] = useState(props.data.show);


    const handleClose = () => {
        if (props.data.redirect != undefined)
            navigate(props.data.redirect, { state: props.data.from });
        props.data.show = false;
        setShow(false);
    };

    // Cierre de etapas
    const [getInfoCierreEtapa_CR, setInfoCierreEtapa_CR] = useState({ data: [], links: [], meta: [] });
    const [getRtaCierreEtapa_CR, setRtaCierreEtapa_CR] = useState(false);

    useEffect(() => {
        async function fetchData() {
            const data = {
                "data": {
                    "type": "log_disciplinario",
                    "attributes": {
                        "id_proceso_disciplinario": '73d8ca3b-9866-4f13-a5d9-2b9203492f16',
                        "id_etapa": 1,
                    }
    
                }
            }
    
            GenericApi.getByDataGeneric('log-proceso-disciplinario/get-log-cierre-etapa', data).then(
                datos => {

                    if (!datos.error) {
                        setInfoCierreEtapa_CR(datos);
                        setRtaCierreEtapa_CR(true);
                    }
                }
            );
    
            window.showSpinner(true);    

        }
        fetchData();
    }, []);

    const listaCierreEtapa = () => {

        if (getInfoCierreEtapa_CR.data != null && typeof (getInfoCierreEtapa_CR.data) != 'undefined') {

            return (

                getInfoCierreEtapa_CR.data.map((etapa, i) => {
                    return (

                        <tr key={etapa.id}>
                            <td>{etapa.attributes.etapa.nombre}</td>
                            <td>{etapa.attributes.created_at}</td>
                            <td>{etapa.attributes.funcionario_registra.nombre} {etapa.attributes.funcionario_registra.apellido}</td>
                            <td>{etapa.attributes.dependencia_origen.nombre} </td>
                            <td>Etapa revisada y aprobada por funcionario(a)</td>
                        </tr>
                    )
                })
            )
        }
    }




    return ( 
        <>
            <Modal show={show} onHide={handleClose}>
                <div className="block block-themed block-transparent mb-0">
                    <Modal.Header className="block-header bg-primary-dark">
                        <Modal.Title className='block-title'> {props.data.title}</Modal.Title>
                        <Button variant="primary" onClick={handleClose} className='btn-block-option'>
                            <i className="fa fa-fw fa-times"></i>
                        </Button>
                    </Modal.Header>
                    <Modal.Body>
                        <div className='row'>
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th>Etapa</th>
                                        <th>Fecha registro</th>
                                        <th>Funcionario Registra</th>
                                        <th>Dependencia</th>
                                        <th>Detalle de cierre</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {/*getRtaCierreEtapa_CR ? listaCierreEtapa() : null*/}
                                </tbody>
                            </table>
                        </div>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={handleClose}>
                            Aceptar
                        </Button>
                    </Modal.Footer>
                </div>
            </Modal>
        </>
    );
}

export default ModalCierreEtapaInfo2;
