import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import { Navigate } from "react-router-dom";
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';

function UsuarioLista() {


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getRespuestaUsuarioSearch, setRespuestaUsuarioSearch] = useState(false);
    const [getUsuarioListaSearch, setUsuarioListaSearch] = useState({ data: [], links: [], meta: [] });

    const [getRespuestaUsuarioTotal, setRespuestaUsuarioTotal] = useState(false);
    const [getUsuarioListaTotal, setUsuarioListaTotal] = useState({ data: [], links: [], meta: [] });

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Nombre',
            cell: usuario => <div>{usuario.attributes.nombre + ' ' + usuario.attributes.apellido}</div>,
            sortable: true,
            width: "15%"
        },
        {
            name: 'Usuario',
            selector: usuario => usuario.attributes.name,
            sortable: true,
            width: "15%"
        },
        {
            name: 'Correo',
            selector: usuario => usuario.attributes.email,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Dependencia',
            selector: usuario => (usuario.attributes.dependencia ? usuario.attributes.dependencia.nombre : ''),
            sortable: true,
            width: "20%"

        },
        {
            name: 'Estado',
            selector: usuario => (usuario.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "10%"
        },
        {
            name: 'Acciones',

            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>


        }
    ];

    const redirectToRoutes = () => {
        return <Navigate to={`/UsuarioLista/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarUsuarios(1, 10);



        }
        fetchData();
    }, []);

    const cargarUsuarios = (page, perPage) => {
        //cargamos todos los usuarios
        GenericApi.getAllGeneric('usuario/usuario-paginate' + '/' + page + '/' + perPage).then(
            datos => {

                if (!datos.error) {
                    if (datos["data"] != "") {
                        setUsuarioListaSearch(datos)
                        setRespuestaUsuarioSearch(true)
                        setUsuarioListaTotal(datos)
                        setRespuestaUsuarioTotal(true)
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);

                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                    window.showSpinner(false);
                }

            }
        )
    }

    const handlePageChange = page => {

        window.showSpinner(true);
        setPageActual(page);
        cargarUsuarios(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarUsuarios(page, newPerPage);

    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const handleSearch = (event) => {

        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setUsuarioListaSearch(getUsuarioListaTotal);
            }
            else {

                var filteredData = {
                    data: getUsuarioListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.nombre
                            + suggestion.attributes.apellido
                            + suggestion.attributes.name
                            + suggestion.attributes.email
                            + (suggestion.attributes.dependencia ? suggestion.attributes.dependencia.nombre : '')
                            + (suggestion.attributes.estado == '0' ? 'Inactivo' : 'Activo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setUsuarioListaSearch(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };

    const enviarDatos = (estado, id) => {

        try {


        } catch (error) {
            window.showSpinner(false);
        }
    }


    const listaUsuarios = () => {

        if (getUsuarioListaSearch.data != null && typeof (getUsuarioListaSearch.data) != 'undefined') {
            return (

                getUsuarioListaSearch.data.map((usuario, i) => {
                    return (
                        cuerpoTabla(usuario)
                    )
                })
            )
        }
        else {
            return (

                getUsuarioListaSearch.map((usuario, i) => {
                    return (
                        cuerpoTabla(usuario)
                    )
                })
            )
        }

    }

    const cuerpoTabla = (usuario) => {
        return (
            <tr key={usuario.id}>
                <td>
                    {usuario.attributes.nombre} {usuario.attributes.apellido}
                </td>
                <td>
                    {usuario.attributes.name}
                </td>
                <td>{usuario.attributes.email}</td>
                <td>{usuario.attributes.dependencia ? usuario.attributes.dependencia.nombre : ""}</td>
                <td>{usuario.attributes.estado == '0' ? 'Inactivo' : 'Activo'}</td>
                <td>

                    <Link to={`/Usuario/${usuario.id}`}>
                        <button type='button' className='btn btn-sm btn-primary' title='Editar'><span className="fas fa-edit"></span></button>
                    </Link>


                </td>
            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////



    return (
        <>




            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>

                    <div className="block block-themed">
                        <div className="col-md-12">

                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">
                                        <li class="breadcrumb-item"> <small>Lista de Usuarios</small></li>
                                    </ol>
                                </nav>
                            </div>

                        </div>
                        <div className="block-header">
                            <h3 className="block-title">Usuario registrados</h3>

                            <Link to={`/Usuario/Add`} >
                                <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar usuario</button>
                            </Link>
                        </div>

                        <div className="block-content">

                            <div className='row'>

                                <div className='col-md-12'>
                                    <div className="form-group ">
                                        <label htmlFor='search'>Buscar: </label>
                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-sm-12">

                                    <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                        columns={columns}
                                        data={getUsuarioListaSearch.data}
                                        perPage={perPage}
                                        page={pageActual}
                                        pagination
                                        noDataComponent="Sin datos"
                                        paginationServer
                                        paginationTotalRows={getUsuarioListaSearch.meta ? getUsuarioListaSearch.meta.total : 1}
                                        onChangePage={handlePageChange}
                                        onChangeRowsPerPage={handlePerRowsChange}
                                        defaultSortFieldId="Nombre"
                                        striped
                                        icon
                                        paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                        defaultSortAsc={false}
                                    />
                                </div>
                            </div>


                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default UsuarioLista;
