import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useEffect, useState } from 'react';
import { Navigate, useLocation } from "react-router";
import { Link } from 'react-router-dom';
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import InfoExitoApiCustom from '../../Utils/InfoExitoApiCustom';
import Spinner from '../../Utils/Spinner';

function DocumentoCierreForm() {

    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let id_etapa = from.id_etapa;
    let id_fase = from.id_fase;
    let vigencia = from.vigencia;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    
    const [isRedirect, setIsRedirect] = useState(false);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const [respuestaSoporteRadicado, setRespuestaSoporteRadicado] = useState(false);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState({ data: [], links: [], meta: [] });
    
    const [inputListArchivos, setInputListArchivos] = useState([{ folios: "", archivo: {}, filebase64: "" }]);

    //var lista_compulsa = [];
    const [getListaCompulsaCopias, setListaCompulsaCopias] = useState([]);

    const [getDocumentoCierreLista, setDocumentoCierreLista] = useState({ data: [], links: [], meta: [] });

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso`} state={{ from: from }}/>;
    }

    useEffect(() => {
        async function fetchData() {
            
            window.showSpinner(true);

            DocumentoSiriusApi.getDocumentacionSiriusByEtapaAndFase(procesoDisciplinarioId, id_etapa, id_fase).then(
                datos => {
                    if (!datos.error) {
                        console.log("Daticos",datos);
                        setDocumentoCierreLista(datos);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)                        
                        setIsRedirect(true);
                    }
                    window.showSpinner(false);
                }
            )
        }
        fetchData();
    }, []);

    /*Funciones*/
    // handle input change
    const handleInputChangeFolios = (e, index) => {
        const { name, value } = e.target;
        const list = [...inputListArchivos];
        list[index][name] = value;
        setInputListArchivos(list);
    };

    const handleInputChangeArchivos = (e, index) => {

        const { name, files } = e.target; 
        const list = [...inputListArchivos];
        list[index][name] = files[0];
        if(files[0]){
            list[index][name] = files[0];
        }
        else{
            list[index][name] = '';
            list[index].filebase64 = '';
            setInputListArchivos(list);
        }
        //setInputListArchivos(list);
        //Conversion a Base64
        Array.from(e.target.files).forEach( archivo => {
            var reader = new FileReader();
            reader.readAsDataURL(archivo);
            reader.onload = function(){
                var arrayAuxiliar = [];
                var base64 = reader.result;
                arrayAuxiliar = base64.split(',');
                //console.log(arrayAuxiliar[1]);
                list[index].filebase64 = arrayAuxiliar[1];
                setInputListArchivos(list);
            }
        })
    }
    
    // handle click event of the Remove button
    const handleRemoveClick = index => {
        const list = [...inputListArchivos];
        list.splice(index, 1);
        setInputListArchivos(list);
    };
    
    // handle click event of the Add button
    const handleAddClick = () => {
        setInputListArchivos([...inputListArchivos, { folios: "", archivo: {}, filebase64: "" }]);
    };

    const handleOnSubmit = (valores) => {

        window.showSpinner(true);

        let band = false;

        // VALIDA QUE NO EXISTAN ARCHIVOS CON EL MISMO NOMBRE
        /*for (let i = 0; i < inputListArchivos.length - 1; i++) {
            for (let j = i + 1; j < inputListArchivos.length; j++) {
                if (inputListArchivos[i].archivo.name === inputListArchivos[j].archivo.name) {
                    setErrorApi("No puede registrar archivos con el mismo nombre.")
                    window.showModal(1)
                    band = true;
                }
            }
        }*/
        
        const attributes = [];

        if(valores.requiere_documento == 'true'){
            if(band === false){

                // PASO 1. VALIDAR CON SIRIUS
                // PASO 1.1 LOGIN Y RADICACION

                for (let i = 0; i < inputListArchivos.length; i++) {
                    // PASO 2. GUARDAR EN BASE DE DATOS
                    if(inputListArchivos[i].folios !== null || inputListArchivos[i].folios !== ''){

                        attributes.push({
                            "id_proceso_disciplinario": procesoDisciplinarioId,
                            "id_etapa": id_etapa,
                            "id_fase": id_fase,
                            "url_archivo": "www.prueba.com",
                            "nombre_archivo": inputListArchivos[i].archivo.name,
                            "estado": "1",
                            "num_folios": inputListArchivos[i].folios,
                            "num_radicado": radicado,
                            "vigencia": vigencia,
                            "extension":getFileExtension(inputListArchivos[i].archivo.name),
                            "peso":inputListArchivos[i].archivo.length,
                            "created_user": nombreUsuario,
                            "descripcion": valores.descripcion_documento,
                            "file64": inputListArchivos[i].filebase64
                        });

                    }
                }

            }
        }
        if(valores.requiere_compulsa == 'true'){

            for (let i = 0; i < getListaCompulsaCopias.length; i++) {
                attributes.push({
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": id_etapa,
                    "id_fase": id_fase,
                    "url_archivo": getListaCompulsaCopias[i].attributes.url_archivo,
                    "nombre_archivo": getListaCompulsaCopias[i].attributes.nombre_archivo,
                    "estado": "1",
                    "num_folios": getListaCompulsaCopias[i].attributes.num_folios,
                    "num_radicado": radicado,
                    "vigencia": vigencia,
                    "extension": getListaCompulsaCopias[i].attributes.extension,
                    "peso": getListaCompulsaCopias[i].attributes.peso,
                    "created_user": nombreUsuario,
                    "descripcion": valores.descripcion_compulsa,
                    "es_compulsa": true,
                    "id_proceso_disciplinario_compulsa": getListaCompulsaCopias[i].attributes.id_proceso_disciplinario,
                    "radicado_compulsa": getListaCompulsaCopias[i].attributes.num_radicado,
                    "vigencia_compulsa": valores.vigencia,
                    "file64": "false"
                });
            }

        }

        const data = {
            "data": {
                "type": "documento_sirius",
                "attributes": attributes
            }
        }

        DocumentoSiriusApi.addDocumentoSirius(data).then(
            datos => {
                if(!datos.error){
                    from.cambiaColorSoporteRadicado = "btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left";
                    window.showModal(2);
                    setIsRedirect(true);
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )

    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function handleListCompulsa(documento, position, value){
        if(value){
            setListaCompulsaCopias([...getListaCompulsaCopias, documento]);
        }
        else{
            let lista_aux = getListaCompulsaCopias;
            lista_aux = lista_aux.filter(data => data.id != documento.id);
            setListaCompulsaCopias(lista_aux);
        }
    }

    const handleClicArchivo = (documento_sirius) => {
        try {
            let nombre_documento = documento_sirius.attributes.nombre_archivo;
            let extension = documento_sirius.attributes.extension;
            let es_compulsa = false;

            if(documento_sirius.attributes.compulsa){
                es_compulsa = true;
            }

            const data = {
                "data": {
                    "type": "documeto_sirius",
                    "attributes": {
                        "radicado":radicado,
                        "vigencia": vigencia,
                        "id_documento_sirius": nombre_documento,
                        "extension": extension,
                        "es_compulsa": es_compulsa
                    }
                }    
            }

            DocumentoSiriusApi.getDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        //console.log(datos.content_type);
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento, extension);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )  
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName,extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName+"."+extension;
        downloadLink.click();
    }

    function buscarExpediente(expediente, vigencia){
        window.showSpinner(true);
        setRespuestaSoporteRadicado(false)
        DocumentoSiriusApi.getDocumentacionSiriusByExpedienteAndVigencia(expediente, vigencia).then(
            datos => {
                if (!datos.error) {
                    setSoporteRadicadoLista(datos)
                    setRespuestaSoporteRadicado(true)
                    window.showModal(6);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
                window.showSpinner(false);
            }
        )
    }

    /*Componentes */
    const componentForm = () => {
        return(
            <Formik
                initialValues={{
                    descripcion_documento: '',
                    descripcion_compulsa: '',
                    requiere_documento: 'false',
                    requiere_compulsa: 'false',
                    expediente: '',
                    vigencia: ''
                }}
                enableReinitialize
                validate={(valores) => {
    
                    let errores = {}
    
                    if(valores.requiere_compulsa == 'true'){
                        if(!valores.expediente){
                            errores.expediente = 'Campo obligatorio'
                        }

                        if(!valores.vigencia){
                            errores.vigencia = 'Campo obligatorio'
                        }
                    }

                    return errores;
                }}
                onSubmit={(valores, {resetForm}) => {
                    console.log("Entro");
                    handleOnSubmit(valores);
                }}>
                    {({ errors, values }) => (
                        <Form>
                            <div className='row'>
                                <div className='col-md-12'>
                                    <div className="form-group text-center">
                                        <label className="d-block">¿Requiere documento?</label>
                                        <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                            <Field type="radio" className="custom-control-input" id="requiere_documento1" name="requiere_documento" value="true"/>
                                            <label className="custom-control-label" htmlFor="requiere_documento1">Si</label>
                                        </div>
                                        <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                            <Field type="radio" className="custom-control-input" id="requiere_documento2" name="requiere_documento" value="false"/>
                                            <label className="custom-control-label" htmlFor="requiere_documento2">No</label>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className='row'>
                                <div className='col-md-12'>
                                    {
                                        values.requiere_documento == 'true'
                                        ?
                                        (
                                            inputListArchivos.map((x, i) => {
                                                return (
                                                    <div key={i}>
                                                        <div className='row'>
                                                            <div className='col-md-12'>
                                                                <div className='row'>
                                                                    <div className='col-md-6'>
                                                                        <input className='form-control' name="folios" placeholder="No. de folios" value={x.folios} onChange={e => handleInputChangeFolios(e, i)} required/>
                                                                    </div>
    
                                                                    <div className='col-md-6'>
                                                                        <label className="custom-file-label" htmlFor="example-file-input-custom" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                        <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i)} required/>
                                                                    </div>
                                                                    {                                                 
                                                                        inputListArchivos.length !== 1 
                                                                        ? 
                                                                        (
                                                                            <div className="col-md-2">  
                                                                                <button className="btn btn-danger" onClick={() => handleRemoveClick(i)}><i className="fas fa-trash-alt col"></i></button>
                                                                            </div>
                                                                        )
                                                                        :
                                                                        null
                                                                    }
                                                                </div>
                                                            </div>
                                                            { /*<div className='col-md-3'>
                                                                <div className='row'>
                                                                    {
                                                                        inputListArchivos.length - 1 === i 
                                                                        ?
                                                                        (
                                                                            <div className="col-md-12">
                                                                                <button type="button" className="btn btn-sm btn-success mr-1 mb-3" onClick={handleAddClick}> <i className="fas fa-plus"></i> Agregar nuevo archivo</button>
                                                                            </div>
                                                                        )
                                                                        :
                                                                        null
                                                                    }
                                                                </div>
                                                            </div> */}
                                                        </div>
                                                        <br></br>
                                                        <div className='row'>
                                                            <div className='col-md-12'>
                                                                <div className="form-group">
                                                                    <Field as="textarea" className="form-control" id="descripcion_documento" name="descripcion_documento" rows="4" placeholder="Descripción de los soportes" required></Field>
                                                                    <ErrorMessage name='descripcion_documento'component={() => (<span className='text-danger'>{errors.descripcion_documento}</span>)} />
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                );
                                            })
                                        )
                                        :
                                        null
                                    }
                                </div>
                            </div>

                            <div className='row'>
                                <div className='col-md-12'>
                                    <div className="form-group text-center">
                                        <label className="d-block">¿Se compulsan copias?</label>
                                        <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                            <Field type="radio" className="custom-control-input" id="compulsa_copias1" name="requiere_compulsa" value="true"/>
                                            <label className="custom-control-label" htmlFor="compulsa_copias1" name="compulsa_copias">Si</label>
                                        </div>
                                        <div className="custom-control custom-radio custom-control-inline custom-control-lg">
                                            <Field type="radio" className="custom-control-input" id="compulsa_copias2" name="requiere_compulsa" value="false"/>
                                            <label className="custom-control-label" htmlFor="compulsa_copias2">No</label>
                                        </div>
                                    </div>
                                </div>
                            </div>                            
                            {
                                values.requiere_compulsa == 'true'
                                ?
                                (                                    
                                    <>
                                        <div className='row'>
                                            <div className='col-md-4'>
                                                <div className="form-group">
                                                    <label htmlFor="expediente">Expediente</label>
                                                    <Field className="form-control" id="expediente" name="expediente" placeholder="Expediente"></Field>
                                                    <ErrorMessage name="expediente" component={() => (<span className="text-danger">{errors.expediente}</span>)} />
                                                </div>
                                            </div>
                                            <div className='col-md-4'>
                                                <div className="form-group">
                                                    <label htmlFor="vigencia">Vigencia</label>
                                                    <Field className="form-control" id="vigencia" name="vigencia" placeholder="Vigencia"></Field>
                                                    <ErrorMessage name="vigencia" component={() => (<span className="text-danger">{errors.vigencia}</span>)} />
                                                </div>
                                            </div>
                                            <div className="col-md-4">  
                                                <button type="button" className="btn btn-primary" onClick={() => buscarExpediente(values.expediente, values.vigencia)} disabled={ !values.expediente || !values.vigencia }><i className="fa fa-fw fa-search"></i> Buscar</button>
                                            </div>
                                        </div>                                       
                                        {
                                            respuestaSoporteRadicado ?
                                            (
                                                soporteRadicadoLista.data.length > 0 
                                                ?
                                                (
                                                    soporteRadicadoLista.data[0].attributes.id_proceso_disciplinario != procesoDisciplinarioId
                                                    ?
                                                    (
                                                        <>
                                                            <div className='row'>
                                                                <div className='col-md-12'>    
                                                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                        <thead>
                                                                            <tr>
                                                                                <th className="text-center">No.</th>
                                                                                <th>Fecha de registro</th>
                                                                                <th>Archivo</th>                                         
                                                                                <th>Acciones</th>                                         
                                                                            </tr>
                                                                        </thead>
                                                                        <tbody>
                                                                            { cargarTabla() }
                                                                        </tbody>
                                                                    </table>
                                                                </div>
                                                            </div>
                                                            <br></br>
                                                            <div className='row'>
                                                                <div className='col-md-12'>
                                                                    <div className="form-group">
                                                                        <Field as="textarea" className="form-control" id="descripcion_compulsa" name="descripcion_compulsa" rows="4" placeholder="Descripción de los soportes" required></Field>
                                                                        <ErrorMessage name='descripcion_compulsa'component={() => (<span className='text-danger'>{errors.descripcion_compulsa}</span>)} />
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </>
                                                    )
                                                    :
                                                    (
                                                        <div className="alert alert-danger alert-dismissable text-center" role="alert">
                                                            <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                            <p className="mb-0"> No se consultar los documentos anexos de este mismo proceso disciplinario </p>
                                                        </div>
                                                    )
                                                )
                                                :
                                                (
                                                    <div className="alert alert-danger alert-dismissable text-center" role="alert">
                                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                                        <p className="mb-0"> El proceso disciplinario consultado no tiene anexos </p>
                                                    </div>
                                                )
                                            )
                                            :
                                            null
                                        }
                                    </>
                                )
                                :
                                null                              
                            }
                            <div className='row'>
                                <div className="col-md-12 text-right">
                                    <button type="submit" className="btn btn-primary" disabled={(values.requiere_compulsa == 'true' && getListaCompulsaCopias.length <= 0)}> <i className="fas fa-save"></i> Registrar</button>
                                    <Link to={`/RamasProceso/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-danger"><i className="fas fa-window-close"></i> Cancelar</button>       
                                    </Link>
                                </div>
                            </div>
                            <br></br>
                        </Form>
                    )}
            </Formik>
        )
    }

    const componentRegistro = () => {
        return (
            <>
                <div className="col-md-12">
                    <div className="alert alert-warning alert-dismissable" role="alert">
                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                        <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   SOPORTE(S) DEL RADICADO </p>
                        <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                        <p className="mb-0">
                            NO puede superar QUINCE (15) Mb de peso/tamaño.
                            Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                        </p>
                        <p className="mb-0">
                            El nombre del archivo debe ser de máximo 40 caracteres.
                        </p>
                    </div>
                </div>
                <div className="col-md-12">
                    { componentForm() }
                </div>
            </>
        )
    }

    const componentLista = () => {
        return (
            <>
                <div className="col-md-12">
                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                        <thead>
                            <tr>
                                <th className="text-center">No.</th>
                                <th>Fecha de registro</th>
                                <th>Archivo</th>
                                <th>Información</th>
                                <th>Acciones</th>                                         
                            </tr>
                        </thead>
                        <tbody>
                            { cargarTablaDocumentoCierre() }
                        </tbody>
                    </table>
                </div>
            </>
        )
    }

    const cargarTabla = () => {
        return (
            soporteRadicadoLista.data.map((documento_sirius, i) => {
                return(
                    <tr key={documento_sirius.id}>
                        <td width="10%" className="text-center">{i + 1}</td>
                        <td width="20%">{documento_sirius.attributes.created_at}</td>
                        <td>
                            {documento_sirius.attributes.nombre_archivo}
                    <button type="button" className="btn btn-sm btn-light" onClick={() => handleClicArchivo(documento_sirius.attributes.num_radicado, documento_sirius.attributes.nombre_archivo)}><i className="fas fa-download col"></i></button>
                        </td>
                        <td>
                            <div className="custom-control custom-checkbox mb-1">
                                <input type="checkbox" className="custom-control-input" id={'example-cb-custom'+i} name={'example-cb-custom'+i} onClick={event => handleListCompulsa(documento_sirius, i, event.target.checked)} />
                                <label className="custom-control-label" htmlFor={'example-cb-custom'+i}>Compulsar</label>
                            </div>
                        </td>
                    </tr>
                )
            })
        )
    }

    const cargarTablaDocumentoCierre = () => {
        return (
            getDocumentoCierreLista.data.map((documento_sirius, i) => {
                return(
                    <tr key={documento_sirius.id}>
                        <td width="10%" className="text-center">{i + 1}</td>
                        <td width="20%">{documento_sirius.attributes.created_at}</td>
                        <td>
                            {documento_sirius.attributes.nombre_archivo + "." + documento_sirius.attributes.extension}
                        </td>
                        <td>
                            { documento_sirius.attributes.compulsa ? ('Se realizó compulsa de datos del SINPROC No: ' + documento_sirius.attributes.compulsa.radicado_compulsa + ' - vigencia (' + documento_sirius.attributes.compulsa.vigencia_compulsa + ')') : 'Se adjunto' }
                        </td>
                        <td>
                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius)}><i className="fas fa-download"></i></button>
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleCallback = (childData) => {
        try {
            //cargarAntecedentes(childData);
        } catch (error) {

        }

    }

    return (
        <>
            { <Spinner /> }
            {isRedirect ? redirectToRoutes() : null}
            { <InfoExitoApiCustom/> }            
            { <InfoExitoApi />}

            <div className="block block-rounded block-bordered">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">

                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                            <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                            <li className="breadcrumb-item"> <small>Documento Cierre</small></li>
                        </ol>
                    </nav>
                </div>
            </div>

            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className='block-header'>
                                <h3 className='block-title'>SINPROC NO {radicado} ::: DOCUMENTO CIERRE</h3>
                            </div>                            
                            <div className='block-content'>
                                { getDocumentoCierreLista.data.length > 0 ? componentLista() : componentRegistro() }
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default DocumentoCierreForm;
