import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';

function InfoRepartoAleatorio() {


    const [getRtaReparto, setRtaReparto] = useState(false);
    const [getListaReparto, setListaReparto] = useState({ data: [], links: [], meta: [] });
    const [getErrorApi, setErrorApi] = useState('');

    useEffect(() => {
        async function fetchData() {

            repartoAleatorio();
         

        }
        fetchData();
    }, []);


    const repartoAleatorio = () => {

        const data = {
            "data": {
                "type": "log_proceso_disciplinario",
                "attributes": {
                    "": "",
                }
            }
        }
        console.log("DATA: "+JSON.stringify(data));
        GenericApi.getByDataGeneric('log-proceso-disciplinario/get-log-reparto-aleatorio', data).then(

            datos => {
                if (!datos.error) {

                    console.log("DATOS: "+datos)
                    console.log("DATOS: "+datos[0]['nombre_funcionario'])
                    setListaReparto(datos)
                    setRtaReparto(true)
                    window.showSpinner(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                    window.showSpinner(false);
                }

            }
        )
    }


    const listaClasificacionRadicado = () => {
        return (

                getListaReparto.map((info, i) => {
                    return (
                        <tr>
                            <td>{i+1}</td>
                            <td>{info['nombre_funcionario'] } {info['apellido_funcionario']} <br/>
                                {info['name'] }
                            </td>
                            <td>{info['dependencia']}</td>
                            <td>{info['rol']}</td>
                            <td>{info['tipo_de_expediente']} <br/> 
                            {
                                (info['id_tipo_expediente'] === '1' && info['sub_tipo_expediente_id'] === '1'?'Copias':null || 
                                 info['id_tipo_expediente'] === '1' && info['sub_tipo_expediente_id'] === '2'?'General':null || 
                                 info['id_tipo_expediente'] === '1' && info['sub_tipo_expediente_id'] === '3'?'Alerta control político':null || 
                                 info['id_tipo_expediente'] === '2' && info['sub_tipo_expediente_id'] === '1'?'Externa':null || 
                                 info['id_tipo_expediente'] === '2' && info['sub_tipo_expediente_id'] === '2'?'Interna':null || 
                                 info['id_tipo_expediente'] === '3' && info['sub_tipo_expediente_id'] === '1'?'Externa':null || 
                                 info['id_tipo_expediente'] === '3' && info['sub_tipo_expediente_id'] === '2'?'Interna':null || 
                                 info['id_tipo_expediente'] === '4' && info['sub_tipo_expediente_id'] === '1'?'Dias':null || 
                                 info['id_tipo_expediente'] === '4' && info['sub_tipo_expediente_id'] === '2'?'Horas':null
                                )
                            }
                            </td>
                            <td>{info['habilitado'] == 1 ? 'Si':'No'}</td>
                            <td>{info['estado'] == 1 ? 'Activo':'Inactivo'}</td>
                            <td>{info['num_casos']}</td>
                            <td>{info['num_casos_total']}</td>
                        </tr>
                    )
                })   
            )
    
    }


    return (

        <div className="block block-themed">
            <div className="block-header">
                <h3 className="block-title"><strong>Informe de reparto aleatorio</strong></h3>
            </div>
            <div className="block-content">
                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                    <thead>
                        <tr>
                            <th className="text-center">No.</th>
                            <th>Funcionario</th>
                            <th>Dependencia</th>
                            <th>Rol</th>
                            <th>Tipo de proceso</th>
                            <th>Habilitado para reparto</th>
                            <th>Estado</th>
                            <th>No. Tipo proceso</th>
                            <th>No. Por usuario</th>

                        </tr>
                    </thead>
                    <tbody>

                        {getRtaReparto ? listaClasificacionRadicado() : null}
                    </tbody>
                </table>
            </div>      
        </div>
    )

}

export default InfoRepartoAleatorio;
