import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useLocation , Link } from 'react-router-dom';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import DataTable from 'react-data-table-component';
import GenericApi from '../../Api/Services/GenericApi';
import { hasAccess , quitarAcentos } from '../../../components/Utils/Common';
import { ParametroModel } from '../../Models/ParametroModel';

function Buscador() {

    const location = useLocation()
    const { from , disable } = location.state;

    const [getColorPrimary, setColorPrimary] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [listaPendientes, setListaPendientes] = useState({ data: [], links: [], meta: [] });
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);

    const [getEstadosProcesoDisciplinario, setEstadosProcesoDisciplinario] = useState({ data: [] });
    const [getRespuestaEstadosProcesoDisciplinario, setRespuestaEstadosProcesoDisciplinario] = useState(false);

    const [getTiposInteresado, setTiposInteresado] = useState({ data: [] });
    const [getRespuestaTiposInteresado, setRespuestaTiposInteresado] = useState(false);

    const [getEtapas, setEtapas] = useState({ data: [] });
    const [getRespuestaEtapas, setRespuestaEtapas] = useState(false);

    const [getVigencias, setVigencias] = useState({ data: [] });
    const [getRespuestaVigencias, setRespuestaVigencias] = useState(false);

    const [getDependencias, setDependencias] = useState({ data: [] });
    const [getRespuestaDependencias, setRespuestaDependencias] = useState(false);

    const [getExpediente , setExpediente] = useState(false);
    const [getagregarVigencia , setagregarVigencia] = useState(false);
    const [getagregarEstadoDelExpediente , setagregarEstadoDelExpediente] = useState(false);
    const [getagregarUbicacionDelExpediente , setagregarUbicacionDelExpediente] = useState(false);
    const [getagregarNombreDisciplinado , setagregarNombreDisciplinado] = useState(false);
    const [getagregarIdentificacionDisciplinado , setagregarIdentificacionDisciplinado] = useState(false);
    const [getagregarAsuntoDelExpediente , setagregarAsuntoDelExpediente] = useState(false);
    const [getagregarSector , setagregarSector] = useState();
    const [getagregarNombreEntidad , setagregarNombreEntidad] = useState(false);
    const [getagregarNombreQuejoso , setagregarNombreQuejoso] = useState(false);
    const [getagregarIdentificacionQuejoso , setagregarIdentificacionQuejoso] = useState(false);
    const [getagregarTipoQuejoso , setagregarTipoQuejoso] = useState(false);
    const [getagregarEtapaDelExpediente , setagregarEtapaDelExpediente] = useState(false);
    const [getagregarDelegada , setagregarDelegada] = useState(false);

    const [getValorExpediente , setValorExpediente] = useState("");
    const [getValorVigencia , setValorVigencia] = useState("");
    const [getValorEstadoDelExpediente , setValorEstadoDelExpediente] = useState("");
    const [getValorUbicacionDelExpediente , setValorUbicacionDelExpediente] = useState("");
    const [getValorNombreDisciplinado , setValorNombreDisciplinado] = useState("");
    const [getValorIdentificacionDisciplinado , setValorIdentificacionDisciplinado] = useState("");
    const [getValorAsuntoDelExpediente , setValorAsuntoDelExpediente] = useState("");
    const [getValorSector , setValorSector] = useState("");
    const [getValorNombreEntidad , setValorNombreEntidad] = useState("");
    const [getValorNombreQuejoso , setValorNombreQuejoso] = useState("");
    const [getValorIdentificacionQuejoso , setValorIdentificacionQuejoso] = useState("");
    const [getValorTipoQuejoso , setValorTipoQuejoso] = useState("");
    const [getValorEtapaDelExpediente , setValorEtapaDelExpediente] = useState("");
    const [getValorDelegada , setValorDelegada] = useState("");


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarEstadosProcesoDisciplinario();
            cargarTiposInteresado();
            CargarEtapas();
            CargarVigencias();
            CargarDependencias();
        }
        fetchData();
    }, []);

    const CargarVigencias = () => {
        GenericApi.getAllGeneric('vigencia').then(
            datos =>{
                if (!datos.error) {
                    setVigencias(datos);
                    setRespuestaVigencias(true);
                }else{
                    setModalState({ title: "BUSCADOR DE EXPEDIENTE :: SELECCIONE LOS CAMPOS A CONSULTAR", message: "Ocurrió un error al intentar consultar las vigencias.", show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            } 
        )
    }

    const cargarEstadosProcesoDisciplinario = () => {
        GenericApi.getAllGeneric('mas-estado-proceso-disciplinario').then(
            datos => {
                if (!datos.error) {
                    setEstadosProcesoDisciplinario(datos);
                    setRespuestaEstadosProcesoDisciplinario(true);
                }
                else{
                    setModalState({ title: "BUSCADOR DE EXPEDIENTE :: SELECCIONE LOS CAMPOS A CONSULTAR", message: "Ocurrió un error al intentar consultar los estados de procesos disciplinarios.", show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    }
    
    const cargarTiposInteresado = () => {
        GenericApi.getGeneric("tipo-interesado").then(
            datos => {
                if (!datos.error) {
                    setTiposInteresado(datos);
                    setRespuestaTiposInteresado(true);
                }
                else {
                    setModalState({ title: "BUSCADOR DE EXPEDIENTE :: SELECCIONE LOS CAMPOS A CONSULTAR", message: "Ocurrió un error al intentar consultar los tipos de interesados.", show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }

            }
        )
    }

    const CargarEtapas = () => {
        GenericApi.getAllGeneric('mas-etapa').then(
            datos =>{
                if (!datos.error) {
                    setEtapas(datos);
                    setRespuestaEtapas(true);
                }else{
                    setModalState({ title: "BUSCADOR DE EXPEDIENTE :: SELECCIONE LOS CAMPOS A CONSULTAR", message: "Ocurrió un error al intentar consultar las etapas.", show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            } 
        )
    }

    const CargarDependencias = () => {
        GenericApi.getByIdGeneric('mas-dependencia-filtrado', global.Constants.ACCESO_DEPENDENCIA.REMITIR_PROCESO).then(
            datos =>{
                if (!datos.error) {
                    setDependencias(datos);
                    setRespuestaDependencias(true);
                    window.showSpinner(false);
                }else{
                    window.showSpinner(false);
                    setModalState({ title: "BUSCADOR DE EXPEDIENTE :: SELECCIONE LOS CAMPOS A CONSULTAR", message: "Ocurrió un error al intentar consultar las dependencias.", show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            } 
        )
    }

    const enviarDatos = () =>{
        window.showSpinner(true);
        if(
        getValorExpediente != "" ||
        getValorVigencia != "" ||
        getValorEstadoDelExpediente != "" ||
        getValorUbicacionDelExpediente != "" ||
        getValorNombreDisciplinado != "" ||
        getValorIdentificacionDisciplinado != "" ||
        getValorAsuntoDelExpediente != "" ||
        getValorSector != "" ||
        getValorNombreEntidad != "" ||
        getValorNombreQuejoso != "" ||
        getValorIdentificacionQuejoso != "" ||
        getValorTipoQuejoso != "" ||
        getValorEtapaDelExpediente != "" ||
        getValorDelegada != "" 
        ){
            console.log("Entro if");
            let procesoDisciplinarioId = from != "" ? from.procesoDisciplinarioId : "" ;
            let data = {
                "data":{
                    "type":"buscador",
                    "attributes": {
                        "procesoDisciplinarioId": procesoDisciplinarioId,
                        "n_expediente": getValorExpediente,
                        "Vigencia": getValorVigencia,
                        "estado_del_expediente": getValorEstadoDelExpediente,
                        "ubicacion_del_expediente": getValorUbicacionDelExpediente,
                        "nombre_disciplinado": getValorNombreDisciplinado,
                        "identificacion_disciplinado": getValorIdentificacionDisciplinado,
                        "asunto_del_expediente": getValorAsuntoDelExpediente,
                        "sector": getValorSector,
                        "nombre_entidad": getValorNombreEntidad,
                        "nombre_quejoso": getValorNombreQuejoso,
                        "identificacion_quejoso": getValorIdentificacionQuejoso,
                        "tipo_quejoso": getValorTipoQuejoso,
                        "etapa_del_expediente": getValorEtapaDelExpediente,
                        "delegada": getValorDelegada
                    }
                }
            }

            console.log(data);
    
            GenericApi.getByDataGeneric('buscador-general', data).then(
                datos => {
                    if (!datos.error) {
                        if(datos.data.length > 0){
                            setListaPendientes(datos);
                            window.showSpinner(false);
                            setModalState({ title: "Buscador de expediente :: Alerta", message: "Busqueda exitosa se encontraron "+datos.data.length+" existencias", show: true, redirect: null, alert: global.Constants.TIPO_ALERTA.EXITO });
                        }else{
                            window.showSpinner(false);
                            setModalState({ title: "Buscador de expediente :: Alerta", message: "No se han encontrado coincidencias", show: true, redirect: null, alert: global.Constants.TIPO_ALERTA.ERROR });
                        }
                        
                        
                    }
                    else {
                        window.showSpinner(false);
                        console.log("Error en la consulta");
                    }
                }
            )

        }else{
            setModalState({ title: "Buscador de expediente :: Alerta", message: "No se envio ningun dato", show: true, redirect: null, alert: global.Constants.TIPO_ALERTA.ERROR });
        }   
        

        
        
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    let columns = [

        {
            name: 'Acciones',
            cell: pendiente =>
                <div>
                { 
                    (pendiente.attributes.migracion == -1? "No es posible migrar la información"
                        :pendiente.attributes.migracion == 1?
                        <Link to={`/ProcesoDisciplinarioMigracion/${pendiente.attributes.Numero_expediente}/${pendiente.attributes.proceso_disciplinario.attributes.vigencia}`}>
                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Iniciar Migración" 
                            data-original-title="Iniciar Migración"><span className="fas fa-download"> </span></button><br></br>
                            <span>{pendiente.attributes.observacion}</span>
                        </Link>:
                    (hasAccess('MP_RamasProceso', 'Consultar')) && pendiente.attributes.proceso_disciplinario != null ?
                    (
                        <Link to={`/RamasProceso/`} state={{from: new ParametroModel(
                                pendiente.attributes.proceso_disciplinario.attributes.radicado,
                                pendiente.attributes.proceso_disciplinario.id,
                                pendiente.attributes.proceso_disciplinario.attributes.vigencia,
                                (pendiente.attributes.proceso_disciplinario.attributes.evaluacion ? pendiente.attributes.proceso_disciplinario.attributes.evaluacion.id : ""),
                                (pendiente.attributes.proceso_disciplinario.attributes.evaluacion ? pendiente.attributes.proceso_disciplinario.attributes.evaluacion.nombre.toUpperCase() : ""),
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                /*(pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario) ? (pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.nombre?pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.nombre:"") + ' ' + (pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.apellido?pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.apellido:"") : "",*/
                                pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente == null ? "":pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.descripcion,
                                pendiente.attributes.proceso_disciplinario.attributes.created_at,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_sinproc == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_sinproc.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_sirius == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_sirius.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_poder_preferente == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_poder_preferente.fecha_ingreso,    
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_desglose == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_desglose.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente ? pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.dependencia.nombre : "",
                        )
                        }}>
                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Ver rama del proceso" data-original-title="Ver rama del proceso"><span className="fas fa-search"> </span></button>
                        </Link>
                    ) : null)
                }
                </div>
        },

        {
            name: 'Dependencia',
            selector: pendiente => pendiente.attributes.Dependencia,
            sortable: true,
        },
        {
            name: 'Radicado',
            selector: pendiente => pendiente.attributes.Numero_expediente,
            sortable: true
        },
        {
            name: 'Vigencia',
            selector: pendiente => pendiente.attributes.proceso_disciplinario==null?null:pendiente.attributes.proceso_disciplinario.attributes.vigencia,
            sortable: true
        },
        {
            name: 'Versión',
            selector: pendiente => pendiente.attributes.Version,
            sortable: true
        },
        {
            name: 'Etapa',
            selector: pendiente => pendiente.attributes.Etapa_expediente,
            sortable: true,
        },
        {
            name: 'Estado',
            selector: pendiente => pendiente.attributes.Estado_expediente,
            sortable: true
        },
        {
            name: 'Fecha D/M/A',
            selector: pendiente => pendiente?.attributes?.proceso_disciplinario?.attributes?.created_at,
            sortable: true
        },
        {
            name: 'Entidad Origen',
            selector: pendiente => pendiente?.attributes?.proceso_disciplinario?.attributes?.dependencia_registro?.nombre,
            sortable: true
        },
        {
            name: 'Nombre interesado',
            selector: pendiente => pendiente?.attributes?.Nombre_interesado,
            sortable: true
        },
        {
            name: 'Identificación interesado',
            selector: pendiente => pendiente?.attributes?.Identificacion_interesado,
            sortable: true
        },
        {
            name: 'Tipo interesado',
            selector: pendiente => pendiente?.attributes?.Tipo_interesado,
            sortable: true
        },
        {
            name: 'Antencedentes',
            selector: pendiente => pendiente?.attributes?.Asunto_del_expediente ? pendiente?.attributes?.Asunto_del_expediente : pendiente?.attributes?.proceso_disciplinario?.attributes?.ultimo_antecedente?.descripcion,
            sortable: true,
            width: "600px"
        },
        {
            name: 'Tipo conducta',
            selector: pendiente => pendiente?.attributes?.Tipo_conducta,
            sortable: true
        },
        {
            name: 'Migracion',
            selector: pendiente => pendiente.attributes.migracion,
            sortable: true
        },
        {
            name: 'Ubicación expediente',
            selector: pendiente => pendiente?.attributes?.Ubicacion_actual,
            sortable: true
        },
        /*
        {
            name: 'Sector',
            selector: pendiente => "-",
            sortable: true
        },
        
        {
            name: 'Nombre disciplinados',
            selector: pendiente => "-",
            sortable: true,
        },
        {
            name: 'Identificacion disciplinados',
            selector: pendiente => "-",
            sortable: true,
        },
        */
        {
            name: 'Cargos',
            selector: pendiente => pendiente?.attributes?.Cargo_interesado,
            sortable: true,
        },
        {
            name: 'Tipo Proceso',
            selector: pendiente => pendiente?.attributes?.proceso_disciplinario?.attributes?.nombre_tipo_expediente,
            sortable: true,
        },
        {
            name: 'Tipo auto',
            selector: pendiente => pendiente?.attributes?.Tipo_auto,
            sortable: true
        },
        {
            name: 'Auto',
            selector: pendiente => pendiente?.attributes?.Auto,
            sortable: true,
        },
        {
            name: 'Fecha auto',
            selector: pendiente => pendiente?.attributes?.Fecha_auto,
            sortable: true
        },
        
        {
            name: 'Acciones',
            cell: pendiente =>
                <div>
                { 
                    (pendiente.attributes.migracion == -1? "No es posible migrar la información"
                        :pendiente.attributes.migracion == 1?
                        <Link to={`/ProcesoDisciplinarioMigracion/${pendiente.attributes.Numero_expediente}/${pendiente.attributes.proceso_disciplinario.attributes.vigencia}`}>
                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Iniciar Migración" 
                            data-original-title="Iniciar Migración"><span className="fas fa-download"> </span></button><br></br>
                            <span>{pendiente.attributes.observacion}</span>
                        </Link>:
                    (hasAccess('MP_RamasProceso', 'Consultar')) && pendiente.attributes.proceso_disciplinario != null ?
                    (
                        <Link to={`/RamasProceso/`} state={{from: new ParametroModel(
                                pendiente.attributes.proceso_disciplinario.attributes.radicado,
                                pendiente.attributes.proceso_disciplinario.id,
                                pendiente.attributes.proceso_disciplinario.attributes.vigencia,
                                (pendiente.attributes.proceso_disciplinario.attributes.evaluacion ? pendiente.attributes.proceso_disciplinario.attributes.evaluacion.id : ""),
                                (pendiente.attributes.proceso_disciplinario.attributes.evaluacion ? pendiente.attributes.proceso_disciplinario.attributes.evaluacion.nombre.toUpperCase() : ""),
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                /*(pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario) ? (pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.nombre?pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.nombre:"") + ' ' + (pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.apellido?pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.usuario.apellido:"") : "",*/
                                pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente == null ? "":pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.descripcion,
                                pendiente.attributes.proceso_disciplinario.attributes.created_at,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_sinproc == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_sinproc.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_sirius == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_sirius.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_poder_preferente == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_poder_preferente.fecha_ingreso,    
                                pendiente.attributes.proceso_disciplinario.attributes.proceso_desglose == null ? "" : pendiente.attributes.proceso_disciplinario.attributes.proceso_desglose.fecha_ingreso,
                                pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente ? pendiente.attributes.proceso_disciplinario.attributes.ultimo_antecedente.dependencia.nombre : "",
                        )
                        }}>
                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Ver rama del proceso" data-original-title="Ver rama del proceso"><span className="fas fa-search"> </span></button>
                        </Link>
                    ) : null)
                }
                </div>
        }

    ];

    let columnsBase = [
        {
            name: 'Dependencia',
            selector: pendiente => pendiente.attributes.Dependencia,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'Número expediente',
            selector: pendiente => pendiente.attributes.Numero_expediente,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'Estado expediente',
            selector: pendiente => pendiente.attributes.Estado_expediente,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'Ubicación expediente',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Entidad origen',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Nombre interesado',
            selector: pendiente => pendiente.attributes.Nombre_interesado,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'Sector',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Nombre disciplinados',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Etapa expediente',
            selector: pendiente => pendiente.attributes.Etapa_expediente,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'Fecha auto',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Auto',
            selector: pendiente => pendiente.attributes.Auto,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Tipo de auto',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "7.7%"
        },
        {
            name: 'Asunto del expediente',
            selector: pendiente => pendiente.attributes.Asunto_del_expediente,
            sortable: true,
            //width: "7.7%"
        },
        {
            name: 'ACCIONES',
            cell: pendiente => pendiente.attributes.Asunto_del_expediente,
            width: "29%"
        }

    ];
    
    
    /* Agregar inputs a la busqueda */

        const agregarExpediente = (value, cheked) =>{
            if(cheked){
                setExpediente(true);
            }else{
                setExpediente(false);
                setValorExpediente("");
            }
        }

        const agregarVigencia = (value, cheked) =>{
            if(cheked){
                setagregarVigencia(true);
            }else{
                setagregarVigencia(false);
                setValorVigencia("");
            }
        }

        const agregarEstadoDelExpediente = (value, cheked) =>{
            if(cheked){
                setagregarEstadoDelExpediente(true);
            }else{
                setagregarEstadoDelExpediente(false);
                setValorEstadoDelExpediente("");
