import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import Spinner from '../Utils/Spinner';
import ModalInfo from '../Utils/Modals/ModalInformacion';
import DataTable from 'react-data-table-component';
import ListaBotones from '../Utils/ListaBotones';
import '../Utils/Constants';
import { quitarAcentos } from '../Utils/Common';

function DepartamentoLista() {
    const [departamentoLista, setDepartamentoLista] = useState({ data: [] });
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getSeach, setSeach] = useState('');
    const [getEstadoLista, setEstadoLista] = useState('1');
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false, button: false });
    const [getRoutes] = useState({
        crear_registro: "/Departamento/Add",
        consultar_registros: "/Departamento",
        muestra_atras: false,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        muestra_inactivos: true,
        modulo: "ADMIN_Otros",
    });

    const handleCallback = (childData) => {
        setEstadoLista(childData);
        cargarDepartamentos(childData);
    }
    const columns = [
        {
            name: 'DEPARTAMENTO',
            selector: departamento => departamento.attributes.nombre,
            sortable: true,
            width: "65%"
        },
        {
            name: 'CÃ“DIGO DANE',
            selector: departamento => departamento.attributes.codigo_dane,
            sortable: true,
            width: "15%"
        },
        {
            name: 'ESTADO',
            selector: departamento => (departamento.attributes.estado == "1" ? 'ACTIVO' : 'INACTIVO'),
            sortable: true,
            width: "10%"
        },
        {
            name: 'ACCIONES',

            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
            ,width: "10%"
        } 
    ];

    useEffect(() => {
        async function fetchData() {
            cargarDepartamentos(1);
        }
        fetchData();
    }, []);


    const cargarDepartamentos = (estado) => {
        window.showSpinner(true);
        GenericApi.getAllGeneric('getDepartamentos/'+estado).then(
            datos => {
                if (!datos.error) {
                    setDepartamentoLista(datos);
                    window.showSpinner(false);
                }else {
                    window.showSpinner(false);
                    setModalState({ title: "LISTA DE DEPARTAMENTOS :: ERROR", message: datos.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR, button: true });
                }
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }

    return (
        <div>

            <Spinner />
            <ModalInfo data={getModalState} />

            <div className="col-md-12">
                <div className="block-content">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li className="breadcrumb-item"> <small>Administrador</small></li>
                            <li className="breadcrumb-item"> <small>Otros</small></li>
                            <li className="breadcrumb-item"> <small>LocalizaciÃ³n</small></li>
                            <li className="breadcrumb-item"> <small>Departamentos</small></li>
                        </ol>
                    </nav>
                </div>
            </div>
            
            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">ADMINISTRACIÃ“N :: DEPARTAMENTOS {getEstadoLista == global.Constants.ESTADOS.INACTIVO ? "INACTIVOS" : "ACTIVOS"}</h3>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-3'>
                            <div className="form-group ">
                                <input type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                        <ListaBotones getRoutes={getRoutes} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                       
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={departamentoLista.data.filter((suggestion) => {
                                    if (getSeach === "") {
                                        if(suggestion.attributes.estado == getEstadoLista){
                                            return suggestion;
                                        }
										
                                    } else if (
                                        ((suggestion.id
                                            + quitarAcentos(suggestion.attributes.nombre)
                                            + suggestion.attributes.codigo_dane
                                            + (suggestion.attributes.estado == "1" ? 'ACTIVO' : 'INACTIVO')).toLowerCase().includes(quitarAcentos(getSeach.toLowerCase()))
                                            && (suggestion.attributes.estado == getEstadoLista))) {
                                        return suggestion;
                                    }
                                })}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationTotalRows={departamentoLista.data.length}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por pÃ¡gina' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DepartamentoLista;
