import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import DataTable from 'react-data-table-component';
import ListaBotones from '../../Utils/ListaBotones';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';

function ActuacionesSigueImpedimentos() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getArchivosListaSearch, setArchivosListaSearch] = useState({ data: [], links: [], meta: [] });
    const [getAuto, setAuto] = useState('');
    const [getNombreDelDocumento, setNombreDelDocumento] = useState('');
    const [getMuestraBotonFechaParaSemaforo, setMuestraBotonFechaParaSemaforo] = useState(false);
    const [getidMasActuacion, setidMasActuacion] = useState();

    const location = useLocation()
    const { getDespuesAprobacionListarActuacion, getActuacionCierraProceso, from, selected_id_etapa, nombre, uuid_actuacion, estadoActualActuacion, tipoActuacion } = location.state

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let estado = 1;

    const [getRoutes, setRoutes] = useState({
        crear_registro: "/ActuacionesForm/",
        consultar_registros: "/ActuacionesLista/",
        ver_detalle: "/ActuacionesVer/" + procesoDisciplinarioId + "/" + selected_id_etapa + "/" + estado,
        seleccionarDueno: "/SeleccionarDuenoProceso/",
        muestra_atras: true,
        id_etapa: global.Constants.ETAPAS.NINGUNA,
        selected_id_etapa: selected_id_etapa,
        muestra_inactivos: true,
    });

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            // Buscamos el auto de la actuacion
            GenericApi.getGeneric("actuaciones/" + uuid_actuacion).then(
                datos => {

                    if (!datos.error) {
                        setAuto(datos.data.attributes.auto);
                    }
                }
            )

            // Buscamos el detalle de los documentos
            GenericApi.getGeneric("archivo-actuaciones/get-archivo-actuaciones-by-uuid/" + uuid_actuacion).then(
                datos => {
                    if (!datos.error) {
                        if (datos.data.length > 0) {
                            setArchivosListaSearch(datos.data[0]);
                            setNombreDelDocumento(datos.data[0].attributes.nombre_archivo);
                        }
                    } else {
                        // cargarHistorialCambios(id);
                        // setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: '/DatosInteresadoLista', from: { from } });
                    }
                }
            )

            GenericApi.getGeneric('mas_actuaciones/getActuacionesByName/' + nombre + '/' + selected_id_etapa).then(
                datos => {
                    if (!datos.error) {
                        setidMasActuacion(datos.data[0].id)
                        GenericApi.getGeneric('semaforo').then(
                            datosSemaforo => {
                                if (!datosSemaforo.error) {
                                    datosSemaforo.data.forEach(element => {
                                        if (element.attributes.id_mas_actuacion_inicia != null) {
                                            if (element.attributes.id_mas_actuacion_inicia.id == datos.data[0].id) {
                                                setMuestraBotonFechaParaSemaforo(true);
                                            }
                                        }
                                    });
                                    window.showSpinner(false);
                                } else {

                                }
                            }
                        )
                    } else {

                    }
                }
            )
        }
        fetchData();
    }, []);

    const redirectToRoutes = () => {
        return <Navigate to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }} />;
    }

    const handleDownloadArchivo = () => {
        try {
            window.showSpinner(true);
            // console.log(getArchivosListaSearch);
            // Se genera la peticion para descargar el archivo
            GenericApi.getGeneric("archivo-actuaciones/get-documento/" + getArchivosListaSearch.attributes.id + "/" + getArchivosListaSearch.attributes.extension).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, getArchivosListaSearch.attributes.nombre_archivo);
                    } else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            console.error(error);
        }
    }

    function downloadBase64File(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const continuar = () => {
        // Se activa el loading
        window.showSpinner(true);

        // si la actuacion tiene habilitada la opcion de cerrar el proceso
        if (getActuacionCierraProceso == 1) {
            const datadata = {
                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": from.procesoDisciplinarioId,
                        "id_etapa": selected_id_etapa,
                        "id_funcionario_asignado": "",
                        "descripcion": "",
                        "created_user": ""
                    }
                }
            }
            GenericApi.addGeneric("cierre-etapa/actuaciones", datadata)
        }

        window.showSpinner(false);
        // Si la actuacion tiene habilitada la opcion de inactivar actuaciones
        if (getDespuesAprobacionListarActuacion == 1) {
            setModalState({
                title: "SINPROC No " + from.radicado + " :: Estado de actuación",
                message: "¡Aprobación exitosa!",
                show: true, redirect: '/ActuacionesInactivar',
                from: { from: from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: uuid_actuacion, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion },
                alert: global.Constants.TIPO_ALERTA.EXITO
            });
        } else {
            if (getMuestraBotonFechaParaSemaforo) {
                setModalState({
                    title: "SINPROC No " + from.radicado + " :: Estado de actuación",
                    message: "¡Aprobación exitosa!",
                    show: true,
                    redirect: '/SeleccionDeFechaParaSemaforo',
                    from: { from: from, selected_id_etapa: selected_id_etapa, id: uuid_actuacion, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion },
                    alert: global.Constants.TIPO_ALERTA.EXITO
                });
            } else {
                setModalState({
                    title: "SINPROC No " + from.radicado + " :: Estado de actuación",
                    message: "¡Aprobación exitosa!",
                    show: true,
                    redirect: '/Transacciones',
                    from: { from: from, selected_id_etapa: selected_id_etapa, id_actuacion: getidMasActuacion },
                    alert: global.Constants.TIPO_ALERTA.EXITO
                });
            }


        }
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}

            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Actuacion {nombre}</small></li>
                            </ol>
                        </nav>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Estado de la actuación</strong></h3>
                        </div>
                        <div className="block-content">
                            <div className='row'>
                                <div className='col-md-12'>
                                    <p>Se ha generado el número de auto {getAuto != '' ? getAuto : null} para el documento {getNombreDelDocumento != '' ? getNombreDelDocumento : null}.</p>
                                    <p>La aprobación finalizará cuando los usuarios incluidos para firma aprueben el firmado del documento.</p>
                                    <button type='button' title='Descargar' className='btn btn-rounded btn-primary' onClick={() => handleDownloadArchivo()}>Descargar documento diligenciado en Word con número de auto <i className="fas fa-download"></i> </button>
                                </div>
                            </div>

                            <div className="row">
                                <div className='col-md-12'>

                                </div>
                            </div>

                            <div className="block-content block-content-full text-right">
                                <button type="button" className="btn btn-rounded btn-primary" onClick={continuar}> Continuar</button>
                            </div>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );

}

export default ActuacionesSigueImpedimentos;
