import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';

function GeneroLista() {
    const [generoLista, setGeneroLista] = useState({ data: [] });
    const [generoListaTotal, setGeneroListaTotal] = useState({ data: [], links: [], meta: [] });

    useEffect(() => {
        async function fetchData() {
            GenericApi.getAllGeneric('genero').then(
                datos => {
                    if (!datos.error) {
                        setGeneroLista(datos);
                        setGeneroListaTotal(datos);
                    }
                    else
                        window.showModal()
                }
            )
        }
        fetchData();
    }, []);

    const genero = () => {
        return (
            generoLista.data.map((genero, i) => {
                return (
                    <tr key={genero.id}>
                        <td>{genero.attributes.nombre}</td>
                        <td>
                            {genero.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td> 
                        <td>
                            <Link to={`${genero.id}`}>
                                <button type="button" className="btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link> 
                            {/* <button type="button" className="btn btn-danger mr-1 mb-3">
                                <i className="fa fa-fw fa-trash-alt"></i>
                            </button> */}
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setGeneroLista(generoListaTotal);
            }
            else {
                var filteredData = {
                    data: generoListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()
                        ))
                    )
                };
                setGeneroLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}

            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de Genero</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Genero registrados</h3>
                    <Link to={`/Genero/Add`} >
                        <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Genero</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th>Nombre</th>
                                        <th>Estado</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {genero()}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default GeneroLista;
