import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import '../../Utils/Constants';
import { useLocation } from 'react-router-dom';
import DataTable from 'react-data-table-component';
import GenericApi from './../../Api/Services/GenericApi';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { quitarAcentos } from '../../Utils/Common';
import 'react-datetime/css/react-datetime.css';
import DatePicker from 'react-datetime';
import moment from 'moment';


function ListaSemaforos() {

    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [getSeach, setSeach] = useState('');
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaSemaforos, setListaSemaforos] = useState({ data: [] });
    const [getLista, setLista] = useState({ data: [] });
    const [ConfirmarListaSemaforos, SetConfirmarListaSemaforos] = useState(0);
    const [getNombreProceso, setNombreProceso] = useState('');


    const location = useLocation();
    const { from } = location.state;
    let [getMasEvento, setMasEvento] = useState('');

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            nombreProceso();
        }
        fetchData();
    }, []);


    const nombreProceso = () => {

        GenericApi.getByIdGeneric("nombre-proceso",from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setNombreProceso(datos.data.attributes.nombre);
                    getSemaforosPorProceso();
                }
            }
        )
    }

    const getSemaforosPorProceso = () => {

        GenericApi.getByIdGeneric('getSemaforosPorProceso', from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    datos.data.forEach(element => {
                        GenericApi.getByIdGeneric('semaforo', element.attributes.id_semaforo.id).then(
                            datosSemaforo => {
                                if (!datosSemaforo.error) {
                                    getListaSemaforos.data.push(datosSemaforo.data);
                                    SetConfirmarListaSemaforos(getListaSemaforos.data.length);
                                    
                                }else{
                                    setModalState({ title: "Lista de Semáforos", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                                }
                                
                            }
                        )
                    });
                    
                    window.showSpinner(false);
                }else{
                    setModalState({ title: "Lista de Semáforos", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                }
            }
        )

        Calcular();
    
    }

    const Calcular = () => {
        GenericApi.getByIdGeneric('getDiasTranscurridos', from.procesoDisciplinarioId).then(
            datosDiasTranscurridos => {
                if (!datosDiasTranscurridos.error) {
                    console.log(datosDiasTranscurridos);
                    setLista(datosDiasTranscurridos);
                }
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }


    const SemaforoColor = (semaforo) => {
        let color = '';
        semaforo.attributes.pdxsemaforo.attributes.condiciones.forEach(condiciones => {
            
            let diasTrans = parseInt(semaforo.attributes.diasTranscurridos.slice(1), 10);
            let inicial = parseInt(condiciones.inicial, 10);
            let final = condiciones.final != null ? parseInt(condiciones.final, 10) : (diasTrans+1);

            if(semaforo.attributes.pdxsemaforo.attributes.finalizo != null){
                color = "Azul";
            }else{
                if(diasTrans >= inicial && diasTrans <= final){
                    color = condiciones.color ;
                 }
            }
        })

        return color;
    }

    const changeFechaInicioSemaforo = (semaforo,date) => {
        const userInput = moment(date).format("YYYY-MM-DD");
        console.log(semaforo.attributes.pdxsemaforo.attributes.id_semaforo.id);
        console.log(from.procesoDisciplinarioId);
        console.log(userInput);

        GenericApi.getGeneric("actualizar-fecha/"+semaforo.attributes.pdxsemaforo.attributes.id_semaforo.id+"/"+from.procesoDisciplinarioId+"/"+userInput).then(
            datosSemaforo => {
                if (!datosSemaforo.error) {
                    setModalState({ 
                        title: getNombreProceso + " :: Selección de fecha de inicio de semáforo", 
                        message: 'Fecha seleccionada actualizada con éxito', 
                        show: true, 
                        redirect: '/MisPendientes', 
                        from: {from: from}, 
                        alert: global.Constants.TIPO_ALERTA.EXITO 
                    });
                }else{
                    setModalState({ 
                        title: getNombreProceso + " :: Selección de fecha de inicio de semáforo", 
                        message: 'Ocurrio un error al intentar actualizar la fecha', 
                        show: true, 
                        redirect: '/MisPendientes', 
                        from: {from: from}, 
                        alert: global.Constants.TIPO_ALERTA.ERROR 
                    });
                }
            }
        )
    }
    
    const columns = [
        {
            name: 'Nombre',
            selector: semaforo => semaforo.attributes.pdxsemaforo.attributes.id_semaforo.nombre,
            sortable: true
        },
        {
            name: 'Fecha Inicio del semaforo',
            selector: semaforo => 
            (
                <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                }}
            >

                {({ errors }) => (
                    <Form>
                            <div className="form-group" id="contenedorFechaSemaforo">        
                                <DatePicker id="fechaInicioSemaforo" locale='es' name="fechaInicioSemaforo" dateFormat="YYYY-MM-DD" closeOnSelect={true} placeholder="yyyy-mm-dd" onChange={(date) => changeFechaInicioSemaforo(semaforo,date)} timeFormat={false} value={semaforo.attributes.pdxsemaforo.attributes.fecha_inicio != null ? semaforo.attributes.pdxsemaforo.attributes.fecha_inicio : null } />
                            </div>
                    </Form>
                )}
            </Formik>
            )
            ,
            sortable: true
        },
        {
            name: 'Fecha de finalización',
            selector: semaforo =>  semaforo.attributes.pdxsemaforo.attributes.fechafinalizo != null ? semaforo.attributes.pdxsemaforo.attributes.fechafinalizo : null,
            sortable: true
        },
        {
            name: 'Evento de inicio',
            selector: semaforo =>  semaforo.attributes.nombreEventoInicio,
            sortable: true
        },
        {
            name: 'Actuación que inicia el semáforo',
            selector: semaforo => semaforo.attributes.nombreMasActuacion,
            sortable: true
        },
        {
            name: 'Días',
            selector: semaforo => semaforo.attributes.pdxsemaforo.attributes.finalizo != null? "-" : semaforo.attributes.diasTranscurridos.slice(1),
            sortable: true
        },
        {
            name: 'Color',
            selector: semaforo => 
            <div>
                {SemaforoColor(semaforo) == "Verde" ? 
                (
                    <div style={{"background-color": "lime", "color":"white","width":"100%","text-align": "center"}}><strong>Verde</strong></div>
                ) 
                : SemaforoColor(semaforo) == "Amarillo" ? 
                (
                    <div style={{"background-color": "yellow", "color":"white","width":"100%","text-align": "center"}}><strong>Amarillo</strong></div>
                ) 
                : SemaforoColor(semaforo) == "Rojo" ? 
                (
                    <div style={{"background-color": "red", "color":"white","width":"100%","text-align": "center"}}><strong>Rojo</strong></div>
                )
                : SemaforoColor(semaforo) == "Azul" ? 
                (
                    <div style={{"background-color": "#0071a1", "color":"white","width":"100%","text-align": "center"}}><strong>Finalizado</strong></div>
                ) 
                : null}
            </div>
            ,
            sortable: true
        }
    ];

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                        
                        <div className="block block-themed">

                            <div className="block-header">
                                <h3 className="block-title">{getNombreProceso} :: Listado de semáforos</h3>
                            </div>

                            <div className="block-content block-content-full">
                                <div className='row'>
                                    <div className='col-md-12'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="block-content block-content-full">
                                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={getLista.data.filter((suggestion) => {
                                                    return suggestion;
                                                })}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent="Sin datos"
                                                paginationTotalRows={getLista.data.length}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                defaultSortFieldId="Nombre"
                                                striped
                                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                                defaultSortAsc={false}
                                            />
                                        
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default ListaSemaforos;
