import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';

function AntecedentesCambiarEstadoForm() {

    const [countTextArea, setCountTextArea] = useState(0);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    let { idAntecedente } = useParams();


    const [getAntecedente, setAntecedente] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoAntecedente, setRtaInfoAntecedente] = useState(false);
    const [getEstadoAntecedente, setEstadoAntecedente] = useState("activa");
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });


    useEffect(() => {
        async function fetchData() {

            setNombreUsuario(getUser().nombre);

            GenericApi.getByIdGeneric("antecedentes", idAntecedente).then(

                datos => {
                    if (!datos.error) {

                        if (datos.data.attributes.estado == true) {
                            setEstadoAntecedente("inactiva");
                        }
                        setAntecedente(datos);
                        setRtaInfoAntecedente(true);
                    }
                    else {
                        setModalState({ title: "SINPROC No "+radicado+" :: Activar / inactivar antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });

                    }

                }
            )
            obtenerParametros();
        }
        fetchData();
    }, []);


    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }


    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "SINPROC No "+radicado+" :: Actualizar antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }
    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "update_user": nombreUsuario,
                    "update_at": Date.now(),
                    "estado_observacion": datos.descripcion,
                    "estado": global.Constants.ESTADOS.ACTIVO,
                }
            }
        }

       GenericApi.updateGeneric("antecedentes",idAntecedente,data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Activar / inactivar antecedentes", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                    window.showSpinner(false);

                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Activar / inactivar antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                    
                }
            }
        )
    }

    const CargarInfoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (
                <tr>
                    <td style={{ width: "20%" }}>
                        <strong>Funcionario: </strong>{getAntecedente.data.attributes.nombre_completo}<br />
                        <strong>Etapa: </strong>{getAntecedente.data.attributes.etapa.nombre}
                        <strong>Fecha: </strong>{getAntecedente.data.attributes.fecha_creado}
                    </td>
                    <td style={{ width: "20%" }}>{getAntecedente.data.attributes.nombre_dependencia}</td>
                    <td style={{ width: "60%" }} data-toggle="popover" data-placement="top" title={getAntecedente.data.attributes.descripcion}>{getAntecedente.data.attributes.descripcion_corta}</td>
                </tr>
            )
        }

    }

    return (
        <>
            {<Spinner/>}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)


                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < getMinimoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMinimoTextArea + ' caracteres'
                    }
                    else if (valores.descripcion.length > getMaximoTextArea) {
                        errores.descripcion = 'Debe ingresar mínimo ' + getMaximoTextArea + ' caracteres'
                    }

                    if (valores.descripcion) {
                        if (containsSpecialChars(valores.descripcion))
                            errores.descripcion = 'Tiene caracteres inválidos'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/AntecedentesLista/`} state={{ from: from }}><small>Antecedentes</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Cambiar estado de antecedente</small></li>
                                </ol>
                            </nav>
                        </div>


                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: <strong>Inactivar Antecedente</strong></h3>
                            </div>
                            <div className="block-content">


                                <div className='col-md-12'>

                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>
                                                <th>Registrado por</th>
                                                <th>Dependencia</th>
                                                <th>Descripción</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {getRtaInfoAntecedente ? CargarInfoAntecedente() : null}

                                        </tbody>
                                    </table>
                                </div>

                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Motivo por el que {getEstadoAntecedente} el antecedente <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Escriba en este espacio la observación"
                                            maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default AntecedentesCambiarEstadoForm;
