import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';

function FormatosLista() {
    const [formatosLista, setFormatosLista] = useState({ data: [] });
    const [formatosListaTotal, setFormatosListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const columns = [
        {
            name: 'Nombre',
            selector: formato => formato.attributes.nombre,
            sortable: true,
            width: "30%"
        },
        {
            name: 'Estado',
            selector: formato => (formato.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarFormatos(1, 10);

        }
        fetchData();
    }, []);

    const cargarFormatos = (page, perPage) => {

        GenericApi.getAllGeneric('mas-formato/formato-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setFormatosLista(datos);
                    setFormatosListaTotal(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal();
                    window.showSpinner(false);
                }

            }
        )

    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarFormatos(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarFormatos(page, newPerPage);

    }

    const formatos = () => {
        return (
            formatosLista.data.map((formato, i) => {
                return (
                    <tr key={formato.id}>
                        <td>{formato.attributes.nombre}</td>
                        <td>
                            {formato.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${formato.id}`}>
                                <button type="button" className="btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>
                            {/* <button type="button" className="btn btn-danger mr-1 mb-3">
                                <i className="fa fa-fw fa-trash-alt"></i>
                            </button> */}
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setFormatosLista(formatosListaTotal);
            }
            else {
                var filteredData = {
                    data: formatosListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()
                            ))
                    )
                };
                setFormatosLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}

            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de Formatos</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Formatos registrados</h3>
                    <Link to={`/Formatos/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar Formato</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={formatosLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={formatosLista.meta ? formatosLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default FormatosLista;
