import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import { useParams } from "react-router";
import ComponenteMDatosInteresado from './ComponenteMDatosInteresado';

function DatoInteresadoMigracionLista() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaDatosInteresado, setListaDatosInteresado] = useState({ data: [], links: [], meta: [] });

    let { radicado, vigencia} = useParams();

    const [getParametros, setParametros] = useState({
        radicado: radicado,
        vigencia: vigencia,
        item: 0,
    });

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            listaAntecedentes();  
        }
        fetchData();
    }, []);


    const listaAntecedentes= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-lista-interesados/"+radicado+"/"+vigencia).then(
                datos => {

                    if (!datos.error) {
                        setListaDatosInteresado(datos);

                    } else {
                        //setModalState({ title: "Migraci贸n Datos del interesado", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const colorFase = (estado_fase) => {

        if (estado_fase === 1)
          return (global.Constants.SEMAFORIZACION_FASES.RED)
        else if (estado_fase === 2)
          return (global.Constants.SEMAFORIZACION_FASES.ORANGE)
        else
          return (global.Constants.SEMAFORIZACION_FASES.GREEN)
    }

    const lista = () => {

        if (getListaDatosInteresado.data != null && typeof (getListaDatosInteresado.data) != 'undefined') {
            return (

                getListaDatosInteresado.data.map((item, i) => {
                    return (
                        <tr>
                            <td>{i=i+1}</td>
                            <td>{item.attributes.nombreCompleto}</td>
                            <td>{item.attributes.numeroDocumento}</td>
                            <td>{item.attributes.tipoSujetoProcesal}</td>
                            <td><Link className="text-dark" to={`/CargarMigracionInteresado/${radicado}/${vigencia}/${item.attributes.id}`}>
                                <button type="button" className="btn btn-sm btn-secundary w2d_btn-large mr-1 mb-3 text-left">
                                    <i className={colorFase(item.attributes.semaforizacion)}></i> Completar informaci贸n
                                </button></Link>
                            </td>
                        </tr>
                    )
                })
            )
        }

         
    }


    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ProcesoDisciplinarioMigracion/${radicado}/${vigencia}`}><small>Inicio proceso de migraci贸n</small></Link></li>
                        <li class="breadcrumb-item"> <small>Lista de interesados</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Proceso disciplinario :: {radicado} - {vigencia}</h3>
                </div>

                <div className="block-content">
                    
                    <div className="col-md-12">

                    {getListaDatosInteresado.data.length === 0 ? 
                    
                        <ComponenteMDatosInteresado getParametros={getParametros} id="m_componente" name="m_componente" />
                        
                        :

                        <table class="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Documento</th>
                                    <th scope="col">Sujeto Procesal</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {lista()}                 
                            </tbody>
                        </table>
                    }

                    </div>
                </div>
                
            </div>

                   
        </>
    );
}


export default DatoInteresadoMigracionLista;
