import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
import DocumentoSiriusApi from '../Api/Services/DocumentoSiriusApi';
import { Navigate } from "react-router-dom";
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../Models/ParametroModel';
import ProcesoDiciplinarioApi from '../Api/Services/ProcesoDiciplinarioApi';
import { hasAccess } from '../../components/Utils/Common';
import InfoExitoApiTextsCustom from '../Utils/InfoExitoApiTextsCustom';

function RamasProceso() {
    const location = useLocation()
    const { from } = location.state

    const [getRespuestaAntecedente, setRespuestaAntecedente] = useState(false);


    const [errorApi, setErrorApi] = useState('');

    // Color Fases
    const [getColorAntecedente, setColorAntecedente] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorDatosInteresado, setColorDatosInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorClasificacionRadicado, setColorClasificacionRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorEntidadInvestigado, setColorEntidadInvestigado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorSoporteRadicado, setColorSoporteRadicado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorValidarClasificacion, setColorValidarClasificacion] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");
    const [getColorComunicacionInteresado, setColorComunicacionInteresado] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");


    const [getHabilitadaComunicacionInteresado, setHabilitadaComunicacionInteresado] = useState(false);

    // Color Etapas
    const [getColorCapturaReparto, setColorCapturaReparto] = useState("#328DBE");
    const [getEvaluacionReparto, setEvaluacionReparto] = useState("#328DBE");
    const [getEvaluacionRepartoCerrada, setEvaluacionRepartoCerrada] = useState(false);

    const [getBotonColorSuccess, setBotonColorSuccess] = useState("btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left");

    const [getEtapaEvaluacionRepartoCompleta, setEtapaEvaluacionRepartoCompleta] = useState(false);
    const [getEtapaEvaluacionCompleta, setEtapaEvaluacionCompleta] = useState(false);

    const [getRespuestaProcesoDisciplinario, setRespuestaProcesoDisciplinario] = useState(false);

    // Cierre de etapas
    const [getInfoCierreEtapa_CR, setInfoCierreEtapa_CR] = useState({ data: [], links: [], meta: [] });
    const [getRtaCierreEtapa_CR, setRtaCierreEtapa_CR] = useState(false);


    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let debeConsultar = from.debeConsultar;

    //from.estadoActividad = true;

    const [isRedirect, setIsRedirect] = useState(false);

    const [getExitoApiTextsCustom, setExitoApiTextsCustom] = useState({ titulo: null, cuerpo: null, boton: null });



    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            //console.log('from = ' + JSON.stringify(from));

            if (debeConsultar) {
                window.showSpinner(true);
                const proceso_disciplinario = {
                    "data": {
                        "type": "antecedentes",
                        "attributes": {
                            "id_proceso_disciplinario": procesoDisciplinarioId,
                        }
                    }
                }

                ProcesoDiciplinarioApi.getConsultarFasesProcesoDisciplinario(proceso_disciplinario, procesoDisciplinarioId).then(
                    datos => {

                        console.log("PROCESO DISCIPLINARIO");
                        console.log(datos);

                        if (!datos.error) {

                            //consultaAntecedentes();

                            if (datos["data"]["attributes"]["etapa"] === '1') {
                                setColorCapturaReparto("#82b54b");
                                from.cambiaColorCapturaReparto = "#82b54b";
                                let evaluacionRepartoCompleta = true;
                                // Etapa: Captura y reparto Fase: Antecendentes
                                if (datos["data"]["attributes"]["antecedentes"] === true) {
                                    setColorAntecedente(getBotonColorSuccess);
                                    //setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorAntecedentes = getBotonColorSuccess;
                                } else {
                                    evaluacionRepartoCompleta = false;
                                }

                                // Etapa: Captura y reparto Fase: Datos del interesado
                                if (datos["data"]["attributes"]["datos_interesado"] === true) {
                                    setColorDatosInteresado(getBotonColorSuccess);
                                    //setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorDatosInteresado = getBotonColorSuccess;
                                } else {
                                    evaluacionRepartoCompleta = false;
                                }

                                // Etapa: Captura y reparto Fase: Clasificación del radicado
                                if (datos["data"]["attributes"]["clasificacion_radicado"] === true) {
                                    setColorClasificacionRadicado(getBotonColorSuccess);
                                    //setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorClasificacionRadicado = getBotonColorSuccess;
                                } else {
                                    evaluacionRepartoCompleta = false;
                                }

                                // Etapa: Captura y reparto Fase: Entidad del investigado

                                if (datos["data"]["attributes"]["entidad_investigado"] === true) {

                                    setColorEntidadInvestigado(getBotonColorSuccess);
                                    //setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorEntidadInvestigado = getBotonColorSuccess;
                                } else {
                                    evaluacionRepartoCompleta = false;
                                }

                                // Etapa: Captura y reparto Fase: Soporte radicado
                                if (datos["data"]["attributes"]["soporte_radicado"] === true) {
                                    setColorSoporteRadicado(getBotonColorSuccess);
                                    //setEtapaEvaluacionRepartoCompleta(true);
                                    from.cambiaColorSoporteRadicado = getBotonColorSuccess;
                                } else {
                                    evaluacionRepartoCompleta = false;
                                }

                                setEtapaEvaluacionRepartoCompleta(evaluacionRepartoCompleta);

                                if (datos["data"]["attributes"]["etapa_cerrada_captura_reparto"] === true) {
                                    setEvaluacionRepartoCerrada(true);
                                    from.evaluacionRepartoCerrada = true;
                                }
                                //from.evaluacionRepartoCerrada = true;
                                //setEvaluacionRepartoCerrada(true);

                            }


                            if (datos["data"]["attributes"]["etapa"] == 2) {


                                // Trae los datos del cierre de la etapa de captura y reparto

                                // Etapa: Evaluación Fase: validar clasificación
                                if (datos["data"]["attributes"]["validar_clasificacion"] === true) {
                                    setColorValidarClasificacion(getBotonColorSuccess);
                                    setEtapaEvaluacionCompleta(true);
                                } else {
                                    setEtapaEvaluacionCompleta(false);
                                }

                                // Etapa: Evaluación Fase: comunicacion interesado
                                if (datos["data"]["attributes"]["muestra_comunicacion_interesado"] === true) {
                                    setHabilitadaComunicacionInteresado(true);
                                    from.habilitaBotonComunicacionInteresado = getBotonColorSuccess;

                                    //si lo muestra revisamos si debe cambiar el color dependiendo si ya hay registros
                                    if (datos["data"]["attributes"]["comunicacion_interesado"] === true) {
                                        setColorComunicacionInteresado(getBotonColorSuccess);
                                        from.cambiaColorComunicacionInteresado = getBotonColorSuccess;
                                        setEtapaEvaluacionCompleta(true);
                                    }
                                    else {
                                        setEtapaEvaluacionCompleta(false);
                                    }

                                }

                            }

                            console.log("estado etapa captura y reparto: "+getEvaluacionRepartoCerrada);
 

                            consultaCierreEtapa(1);

                            setRespuestaAntecedente(true);
                            from.debeConsultar = false;
                            from.radicado = radicado;
                            window.showSpinner(false);



                        }
                        

                    }

                )
            }
            else {
                setColorAntecedente(from.cambiaColorAntecedentes);
                setColorDatosInteresado(from.cambiaColorDatosInteresado);
                setColorClasificacionRadicado(from.cambiaColorClasificacionRadicado);
                setColorEntidadInvestigado(from.cambiaColorEntidadInvestigado);
                setColorSoporteRadicado(from.cambiaColorSoporteRadicado);
                setHabilitadaComunicacionInteresado(from.habilitaBotonComunicacionInteresado);
                setColorComunicacionInteresado(from.cambiaColorComunicacionInteresado);
                setColorCapturaReparto(from.cambiaColorCapturaReparto);
                setEvaluacionRepartoCerrada(from.getEvaluacionRepartoCerrada);

                setRespuestaAntecedente(true);

                if (from.cambiaColorAntecedentes.includes("succes")
                    && from.cambiaColorDatosInteresado.includes("succes")
                    && from.cambiaColorClasificacionRadicado.includes("succes")
                    && from.cambiaColorEntidadInvestigado.includes("succes")
                    && from.cambiaColorSoporteRadicado.includes("succes")) {

                    setEtapaEvaluacionRepartoCompleta(true);
                }
                else{
                    setEtapaEvaluacionRepartoCompleta(false);
                }
                //window.showSpinner(false);
            }

        }
        fetchData();
    }, []);

    const consultaCierreEtapa = (id_etapa) => {

        const datos = {
            "data": {
                "type": "cierre_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": id_etapa
                }
                
            }
        }

        window.showSpinner(true);

        CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(datos).then(
            datos => {
                if (!datos.error) {
                    setInfoCierreEtapa_CR(datos);
                    setRtaCierreEtapa_CR(true);
                }

                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
            }
        );

    }




    const listaCierreEtapa = () => {

        console.log(getInfoCierreEtapa_CR.data);

        if (getInfoCierreEtapa_CR.data != null && typeof (getInfoCierreEtapa_CR.data) != 'undefined') {
            return (

                getInfoCierreEtapa_CR.data.map((etapa, i) => {
                    return (

                        <tr key={etapa.id}>
                            <td>{i + 1}</td>
                            <td>{etapa.attributes.created_at}</td>
                            <td>{etapa.attributes.etapa.nombre}</td>
                            <td>{etapa.attributes.id_funcionario_asignado}</td>
                            <td>Dependencia</td>
                            <td>{etapa.attributes.created_user}</td>
                            <td>Etapa revisada y aprobada por funcionario(a)</td>
                        </tr>
                    )
                })
            )
        }


    }




    const consultaAntecedentes = () => {

        const dataAntecedente = {
            "data": {
                "type": "antecedente",
                "attributes": {
                    "descripcion": "descripcion",
                    "fecha_registro": "fecha_registro",
                    "id_dependencia": "id_dependencia",
                    "estado": "estado",
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_etapa": "id_etapa",
                    "dependencia_origen": "dependencia_origen",
                    "vigencia": "vigencia",
                    "fecha_creado": "fecha_creado",
                    "etapa": "etapa"
                }
            }
        }

        AntecedentesApi.getPrimerYUltimoAntecedente(dataAntecedente, procesoDisciplinarioId).then(
            datos => {

                if (!datos.error) {

                    console.log("DATOS: " + JSON.stringify(datos));
                    //window.showModal(2)

                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal()
                }
            }
        )

    }

    //Funciones
    const handleClicSoporteRadicado = (id_etapa, id_fase, es_soporte) => {
        from.id_etapa = id_etapa;
        from.id_fase = id_fase;
        changeValueEsFuncion(es_soporte);
    };

    function changeValueEsFuncion(es_funcion){
        from.es_soporte = es_funcion;
    }

    const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_dependencia": 1,
                    "descripcion": "Cierre de etapa",
                    "id_funcionario": 1,
                    "created_user": "ForsecurityDiscUno"
                }
            }
        }

        

        CierreEtapaApi.addCierreEtapa(data).then( 
            datos => {
                if (!datos.error) {

                    setExitoApiTextsCustom(
                        {
                            titulo: 'Asignación del proceso disciplinario',
                            cuerpo: 'El proceso disciplinario es asignado a '+datos.data.attributes.id_funcionario_asignado,
                            boton: 'Aceptar'
                        }
                    );

                    setEvaluacionRepartoCerrada(true);
                    window.showModal(6);

                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }


    const componentAntecedente = () => {


        return (
            <>
               
                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) :: {radicado}</h3>
                        <span><strong>Antecedente: </strong></span><br />
                        <span title={from.antecedente}>{from.antecedente?from.antecedente.substring(0, 300) :""}...</span><br /> 

                        <br />
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>{from.registradoPor}</span></div>
                            <hr />
                            <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {from.dependendencia}</span></div>
                            <hr />
                        </div>

                        <div className="row">

                            <div className="col-lg-4"><span><strong>Vigencia: </strong>{vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de registro: </strong> {moment(from.fechaRegistro, 'DD/MM/YYYY, h:mm:ss a').format("DD/MM/YYYY, h:mm:ss a")}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de ingreso: </strong> {moment(from.fechaIngreso, 'YYYY/MM/DD').format("DD/MM/YYYY")}</span></div>


                        </div>

                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            {<Spinner />}
            {isRedirect ? redirectToRoutes() : null}
            {<InfoExitoApiTextsCustom success={getExitoApiTextsCustom} />}

            <div className="row">

                <div className="col-md-12">
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li className="breadcrumb-item">
                                    <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                            </ol>
                        </nav>

                    </div>
                </div>
                {getRespuestaAntecedente ? componentAntecedente() : null}


                {/*CAPTURA Y REPARTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">


                        <div className="block-header block-header-default" style={{ background: getColorCapturaReparto }}>
                            <h3 className="block-title">Captura y Reparto</h3>
                            <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm" style={{ background: getColorCapturaReparto, color: 'white' }} onClick={() => handleClicSoporteRadicado(1, 1, true)}><i className="fas fa-folder-open fa-2x"></i></button>
                            </Link>
                        </div>
                        <>
                            {
                                (hasAccess('CapturaYReparto', 'Consultar')) ? (
                                    <div className="block-content">

                                        <ul className="nav-items push">

                                            {/*ANTECEDENTES*/}
                                            <li>
                                                <div className='w2d-form-group'>
                                                    <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorAntecedente} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                                    </Link>

                                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                                        <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(1, 1, true)}><i className="fas fa-folder-open"></i></button>
                                                    </Link>
                                                </div>
                                            </li>

                                            {/*DATOS DEL INTERESADO*/}
                                            <li>
                                                <div className="w2d-form-group">
                                                    <Link to={`/DatosInteresadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorDatosInteresado} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-users"></i> Datos del interesado</button>
                                                    </Link>
                                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                                        <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(1, 2, true)}><i className="fas fa-folder-open"></i></button>
                                                    </Link>
                                                </div>
                                            </li>

                                            {/*CLASIFICACION DEL RADICADO*/}
                                            <li>
                                                <div className="w2d-form-group">
                                                    <Link to={`/ClasificacionRadicadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorClasificacionRadicado} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-sitemap"></i> Clasificación del radicado</button>
                                                    </Link>
                                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }} className="text-right">
                                                        <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(1, 3, true)}><i className="fas fa-folder-open"></i></button>
                                                    </Link>
                                                </div>
                                            </li>

                                            {/*ENTIDAD DEL INVESTIGADO*/}
                                            <li>
                                                <div className="w2d-form-group">
                                                    <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorEntidadInvestigado} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-hotel"></i> Entidad del investigado</button>
                                                    </Link>
                                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                                        <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" onClick={() => handleClicSoporteRadicado(1, 4, true)}><i className="fas fa-folder-open"></i></button>
                                                    </Link>
                                                </div>

                                            </li>

                                            {/*SOPORTE RADICADO*/}
                                            <li>
                                                <div className="w2d-form-group">
                                                    <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorSoporteRadicado} onClick={() => handleClicSoporteRadicado(1, 5, false)}><i className="fas fa-archive"></i> Soporte Radicado</button>
                                                    </Link>
                                                </div>
                                            </li>

                                            {/*CERRAR ETAPA*/}
                                            <li>
                                                {(getEtapaEvaluacionRepartoCompleta && !getEvaluacionRepartoCerrada) && (

                                                    <div className="form-group">

                                                        {console.log("ESTOY DENTRO DE CIERRE DE ESTAPA 1: "+getEvaluacionRepartoCerrada)}

                                                        <Formik
                                                            initialValues={{
                                                                inputCierreEtapa: '',
                                                                inputEtapa: 1,
                                                            }}
                                                            enableReinitialize
                                                            validate={(valores) => {

                                                                let errores = {}
                                                                if (!valores.inputCierreEtapa) {
                                                                    errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                                                }
                                                                return errores
                                                            }}

                                                            onSubmit={(valores, { resetForm }) => {
                                                                cerrarEtapa(valores);
                                                            }}
                                                        >

                                                            <Form>
                                                                <div className="col-md-12">
                                                                    <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa" />
                                                                    <label className="form-check-label" htmlFor="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                                                </div>

                                                                <div className="col-md-12">
                                                                    <>
                                                                        {
                                                                            (hasAccess('CR_CierreEtapa', 'Crear')) ? (
                                                                                <div className="block-content block-content-full text-center">
                                                                                    <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                                                </div>
                                                                            ) : null
                                                                        }
                                                                    </>
                                                                </div>
                                                            </Form>
                                                        </Formik>
                                                    </div>
                                                )

                                                }
                                            </li>

                                            {getEvaluacionRepartoCerrada &&   (
                                                <li>
                                                     {console.log("ESTOY DENTRO DE CIERRE DE ESTAPA 2: "+getEvaluacionRepartoCerrada)}

                                                    <div className="col-md-12 text-center">
                                                        <button type='button' title='Consultar cierre de etapa' className='btn btn-sm'
                                                            data-toggle="modal" data-target={'#modal-consultar-detalle'}> <i className="fas fa-clipboard-list fa-2x text-info "></i></button>
                                                    </div>
                                                </li>
                                            )}

                                        </ul>
                                    </div>
                                ) : null
                            }
                        </>


                    </div>
                </div>

                {/*EVALUACIÓN*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default" style={{ background: getEvaluacionReparto }}>
                            <h3 className="block-title">Evaluación</h3>
                        </div>

                        <>
                            {
                                (hasAccess('Evaluacion', 'Consultar')) ? (
                                    <div className="block-content">
                                        <div className="media-body">

                                            <ul className="nav-items push">

                                                {/* VALIDAR CLASIFIACION*/}

                                                <li>
                                                    <Link to={`/ValidarClasificacionRadicadoLista/`} state={{ from: from }}>
                                                        <button type="button" className={getColorValidarClasificacion}><i className="fas fa-archive"></i> Validar Clasificación</button>
                                                    </Link>
                                                    <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i className="fas fa-folder-open"></i></button>
                                                </li>

                                                {
                                                    (getHabilitadaComunicacionInteresado && hasAccess('EI_ComunicacionInteresado', 'Crear')) ? (
                                                        < li >
                                                            <Link to={`/ComunicacionInteresadoForm/`} state={{ from: from }}>
                                                                <button type="button" className={getColorComunicacionInteresado}><i className="fas fa-hotel"></i> Comunicación Interesado(s)</button>
                                                            </Link>
                                                            <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i className="fas fa-folder-open"></i></button>
                                                        </li>
                                                    ) : null
                                                }


                                                {/*REMISION QUEJA*/}
                                                <li>
                                                    <Link to={`/RemisionQuejaForm/`} state={{ from: from }}>
                                                        <button type="button" className={getColorComunicacionInteresado}><i className="fas fa-hotel"></i> Remision Queja</button>
                                                    </Link>
                                                    <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i className="fas fa-folder-open"></i></button>
                                                </li>

                                                {/*GESTOR RESPUESTA*/}
                                                <li>
                                                    <Link to={`/GestorRespuestaForm/`} state={{ from: from }}>
                                                        <button type="button" className={getColorComunicacionInteresado} onClick={() => handleClicSoporteRadicado(2, 9)}><i className="fas fa-hotel"></i> Gestor Respuesta</button>
                                                    </Link>
                                                    <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i className="fas fa-folder-open"></i></button>
                                                </li>

                                                {/*DOCUMENTO CIERRE*/}
                                                <li>
                                                    <Link to={`/DocumentoCierreForm/`} state={{ from: from }}>
                                                        <button type="button" className={getColorComunicacionInteresado} onClick={() => handleClicSoporteRadicado(2, 8)}><i className="fas fa-hotel"></i> Documento Cierre</button>
                                                    </Link>
                                                    <button type="button" className="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i className="fas fa-folder-open"></i></button>
                                                </li>

                                            </ul>

                                        </div>



                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN PD*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Evaluación en PD</h3>
                        </div>
                        <>
                            {
                                (hasAccess('EvaluacionPD', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN PRELIMINAR*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Preliminar</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionPreliminar', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*EVALUACIÓN EN DISCIPLINARIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Investigación Disciplinaria</h3>
                        </div>
                        <>
                            {
                                (hasAccess('InvestigacionDisciplinaria', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Causa / Juzgamiento</h3>
                        </div>
                        <>
                            {
                                (hasAccess('CausaJuzgamiento', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*CAUSA / JUZGAMIENTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Proceso Verbal</h3>
                        </div>
                        <>
                            {
                                (hasAccess('ProcesoVerbal', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                {/*SEGUNDA INSTANCIA*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Segunda Instancia</h3>
                        </div>
                        <>
                            {
                                (hasAccess('SegundaInstancia', 'Consultar')) ? (
                                    <div className="block-content">

                                    </div>
                                ) : null
                            }
                        </>
                    </div>
                </div>

                <div className="modal" id={'modal-consultar-detalle'} tabIndex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                    <div className="modal-dialog modal-xl" role="document">
                        <div className="modal-content">
                            <div className="block block-themed block-transparent mb-0">
                                <div className="block-header bg-primary-dark">
                                    <h3 className="block-title">Sinproc {radicado} :::: Cierre de etapa</h3>
                                    <div className="block-options">
                                        <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                            <i className="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div className="block-content">


                                    <div className='col-md-12'>
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th className="text-center">No</th>
                                                    <th>Fecha registro</th>
                                                    <th>Etapa</th>
                                                    <th>Funcionario Asignado</th>
                                                    <th>Dependencia</th>
                                                    <th>Delegada</th>
                                                    <th>Detalle de cierre</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {getRtaCierreEtapa_CR ? listaCierreEtapa() : null}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right bg-light">
                                    <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>


            </div>

        </>
    );
}


export default RamasProceso;
