import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';
import { quitarAcentos } from '../../Utils/Common';
import { Field, Form, Formik } from 'formik';

function InfoRepartoAleatorio() {


    const [getRtaReparto, setRtaReparto] = useState(false);
    const [getListaReparto, setListaReparto] = useState({ data: [], links: [], meta: [] });
    const [getSearch, setSearch] = useState('');
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);


    const columns = [
        {
            name: 'Registrado por',

            cell: antecedente => <div>
                <strong>Usuario: </strong>{antecedente.attributes.nombre_completo}<br/>
                <strong>Etapa: </strong>{antecedente.attributes.etapa.nombre}<br/>
                <strong>Fecha: </strong>{antecedente.attributes.fecha_creado}<br/>
                <strong>Dependencia: </strong>{antecedente.attributes.nombre_dependencia}<br/></div>,
            selector: antecedente => antecedente.attributes.nombre_completo,
            sortable: true,
            width: "35%"
        },
        {
            name: 'Descripción',

            cell: antecedente =>
                <div title={antecedente.attributes.descripcion}>
                    {antecedente.attributes.descripcion.length > global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION ? (antecedente.attributes.descripcion.substring(0, global.Constants.TEXT_AREA.CANTIDAD_MINIMA_DESCRIPCION) + "...") : (antecedente.attributes.descripcion)}
                </div>,

            selector: antecedente => antecedente.attributes.descripcion,
            sortable: true,
            width: "40%",
        },
        {
            name: 'Estado',
            selector: antecedente => (antecedente.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "10%",
        },        
    ];



    useEffect(() => {
        async function fetchData() {

            repartoAleatorio();
         

        }
        fetchData();
    }, []);


    const handlePageChange = page => {
        setPageActual(page);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        setPerPage(newPerPage);
        setPageActual(page);
    }


    const repartoAleatorio = () => {

        const data = {
            "data": {
                "type": "log_proceso_disciplinario",
                "attributes": {
                    "": "",
                }
            }
        }
        // console.log("DATA: "+JSON.stringify(data));
        GenericApi.getByDataGeneric('log-proceso-disciplinario/get-log-reparto-aleatorio', data).then(

            datos => {
                if (!datos.error) {

                    // console.log("DATOS: "+datos)
                    // console.log("DATOS: "+datos[0]['nombre_funcionario'])
                    setListaReparto(datos)
                    setRtaReparto(true)
                    window.showSpinner(false);
                }
                else {
                    //setErrorApi(datos.error.toString())
                    window.showModal()
                    window.showSpinner(false);
                }

            }
        )
    }


    const listaClasificacionRadicado = () => {
        return (

                getListaReparto.map((info, i) => {
                    return (
                        <tr>
                            <td>{i+1}</td>
                            <td>{info['nombre_funcionario'] } {info['apellido_funcionario']}</td>
                            <td>{info['usuario'] }</td>
                            <td>{info['dependencia']}</td>
                            <td>{info['num_casos_total']}</td>
                            <td>{info['habilitado'] == 1 ? 'Si':'No'}</td>
                            <td>{info['estado'] == 1 ? 'Activo':'Inactivo'}</td>
                            <td>{info['derechos_peticion']}</td>
                            <td>{info['poder_preferente']}</td>
                            <td>{info['queja']}</td>
                            <td>{info['tutela']}</td>
                        </tr>
                    )
                })   
            )
    
    }


    return (

        <div className="block block-themed">
            <div className="block-header">
                <h3 className="block-title"><strong>Informe de reparto aleatorio</strong></h3>
            </div>
            <div className="block-content">


            <div className="row">

                <div className="col-md-6 col-xl-3">
                    <div  className="block block-rounded block-link-shadow bg-primary">
                        <div className="block-content block-rounded block-content-full d-flex align-items-center justify-content-between bg-primary">
                            <div>
                                <i className="fa fa-2x fa-arrow-alt-circle-up text-primary-lighter"></i>
                            </div>
                            <div className="ml-3 text-right">
                                <p className="text-white font-size-h3 font-w300 mb-0">
                                    + 45%
                                </p>
                                <p className="text-white-75 mb-0">
                                    Total de casos
                                </p>
                            </div>
                        </div>
                    </div >
                </div>


                <div className="col-md-6 col-xl-3">
                    <div  className="block block-rounded block-link-shadow bg-primary">
                        <div className="block-content block-rounded block-content-full d-flex align-items-center justify-content-between bg-primary">
                            <div>
                                <i className="fa fa-2x fa-arrow-alt-circle-up text-primary-lighter"></i>
                            </div>
                            <div className="ml-3 text-right">
                                <p className="text-white font-size-h3 font-w300 mb-0">
                                    + 45%
                                </p>
                                <p className="text-white-75 mb-0">
                                    Casos Abiertos
                                </p>
                            </div>
                        </div>
                    </div >
                </div>


                <div className="col-md-6 col-xl-3">
                    <div  className="block block-rounded block-link-shadow bg-primary">
                        <div className="block-content block-rounded block-content-full d-flex align-items-center justify-content-between bg-primary">
                            <div>
                                <i className="fa fa-2x fa-arrow-alt-circle-up text-primary-lighter"></i>
                            </div>
                            <div className="ml-3 text-right">
                                <p className="text-white font-size-h3 font-w300 mb-0">
                                    + 45%
                                </p>
                                <p className="text-white-75 mb-0">
                                    Casos Cerrados
                                </p>
                            </div>
                        </div>
                    </div >
                </div>


                <div className="col-md-6 col-xl-3">
                    <div  className="block block-rounded block-link-shadow bg-primary">
                        <div className="block-content block-rounded block-content-full d-flex align-items-center justify-content-between bg-primary">
                            <div>
                                <i className="fa fa-2x fa-arrow-alt-circle-up text-primary-lighter"></i>
                            </div>
                            <div className="ml-3 text-right">
                                <p className="text-white font-size-h3 font-w300 mb-0">
                                    + 45%
                                </p>
                                <p className="text-white-75 mb-0">
                                    Casos Archivados
                                </p>
                            </div>
                        </div>
                    </div >
                </div>

            </div>

                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                    <thead>
                        <tr>
                            <th className="text-center">No.</th>
                            <th>Nombre</th>
                            <th>Usuario</th>
                            <th>Dependencia</th>
                            <th>Casos asignados</th>
                            <th>Habilitado para reparto</th>
                            <th>Estado</th>
                            <th>Derecho de petición</th>
                            <th>Poder Preferente</th>
                            <th>Queja</th>
                            <th>No. Por usuario</th>
                        </tr>
                    </thead>
                    <tbody>

                        {getRtaReparto ? listaClasificacionRadicado() : null}
                    </tbody>
                </table>

                <Formik>
                <Form>


                <div className='col-md-3'>
                                        <div className="form-group ">
                                            <Field type="text" id="search" value={getSearch} onChange={e => setSearch(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} name="search" className="form-control border border-success" placeholder="Buscar" />
                                        </div>
                                    </div>


                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                columns={columns}
                                                data={getListaReparto.data.filter((suggestion) => {
                                                    if (getSearch === "") {
                                                        return suggestion;
                                                    } else if (

                                                        ((quitarAcentos(suggestion.attributes.descripcion)
                                                            + quitarAcentos(suggestion.attributes.nombre_completo) + quitarAcentos(suggestion.attributes.nombre_dependencia) +
                                                            quitarAcentos(suggestion.attributes.fecha_creado) + quitarAcentos(suggestion.attributes.etapa.nombre) +
                                                            (suggestion.attributes.estado == global.Constants.ESTADOS.ACTIVO ? 'Activo' : 'Inactivo') + suggestion.attributes.fecha_registro +
                                                            quitarAcentos(suggestion.attributes.descripcion)).toLowerCase().includes(quitarAcentos(getSearch.toLowerCase())))

                                                    ) {
                                                        return suggestion;
                                                    }
                                                })}
                                                perPage={perPage}
                                                page={pageActual}
                                                pagination
                                                noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                                                paginationTotalRows={getListaReparto.data.length}
                                                onChangePage={handlePageChange}
                                                onChangeRowsPerPage={handlePerRowsChange}
                                                striped
                                                paginationComponentOptions={{ rowsPerPageText: global.Constants.DATA_TABLE.FILTRO_POR_PAGINA }}
                                            />

                </Form>
                </Formik>
            </div>      
        </div>
    )

}

export default InfoRepartoAleatorio;
