                                                                                                            getGestorRespuestaApi?.data[0]?.attributes?.rol_actual.roles 
                                                                                                        :
                                                                                                            getGestorRespuestaApi?.data[0]?.attributes?.rol_siguiente?.error
                                                                                                    }
                                                                                                </b>
                                                                                                <br></br>
                                                                                                Por: <b>
                                                                                                    { 
                                                                                                        getGestorRespuestaApi?.data[0]?.attributes?.rol_siguiente?.ultimo_usuario == true
                                                                                                    ? 
                                                                                                        getGestorRespuestaApi?.data[0]?.attributes?.rol_actual?.nombre_completo 
                                                                                                    : 
                                                                                                        getGestorRespuestaApi?.data[0]?.attributes?.rol_siguiente?.error
                                                                                                }
                                                                                                </b>
                                                                                                <br></br>
                                                                                            </>
                                                                                    }
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </>
                                                            :
                                                            <>
                                                                { /*MUESTE LA INFORMACION SI LA APROBACION ES RECHAZADA*/ }
                                                                <div className='row'>
                                                                    <div className="col-md-12">
                                                                        <div className="alert alert-danger alert-dismissable" role="alert">
                                                                            <div className='row'>
                                                                                <div className="col-md-3 text-center">
                                                                                    Mi rol: <b>{ getGestorRespuestaApi?.data[0]?.attributes?.rol_actual.roles }</b>
                                                                                </div>
                                                                                {
                                                                                    getGestorRespuestaApi?.data[0]?.attributes?.clasificacion_expediente?.expediente?.id == global.Constants.TIPOS_EXPEDIENTES.TUTELA && getGestorRespuestaApi?.data[0]?.attributes?.rol_anterior?.length > 1
                                                                                    ?
                                                                                        <div className="col-md-9 text-center">
                                                                                            <p>
                                                                                                El usuario que registró la versión del documento que se está gestionando: Personero Delegado <b>{getGestorRespuestaApi?.data[0]?.attributes?.rol_previo?.nombre}</b>; tiene la(s) siguiente(s) novedad(es): 
                                                                                            </p>
