*#__PURE__*/_jsxs("td",{width:"25%",children:[getListaCombinacion[0].nombre_expediente," ",getListaCombinacion[0].nombre_sub_expediente?' - '+getListaCombinacion[0].nombre_sub_expediente:null]}),/*#__PURE__*/_jsx("th",{width:"25%",children:"Tipo de Evaluaci\xF3n: "}),/*#__PURE__*/_jsx("td",{width:"25%",children:getListaCombinacion[0].nombre_evaluacion})]})})})}),/*#__PURE__*/_jsx("div",{className:"block-content",children:/*#__PURE__*/_jsx("div",{className:"row",children:/*#__PURE__*/_jsx("div",{className:"col-md-12",children:/*#__PURE__*/_jsx(DragDropContext,{onDragEnd:handleDragEnd,children:/*#__PURE__*/_jsxs("table",{className:"table table-bordered table-striped table-vcenter js-dataTable-full",children:[/*#__PURE__*/_jsx("thead",{children:/*#__PURE__*/_jsxs("tr",{children:[/*#__PURE__*/_jsx("th",{width:"5%",children:"Mover"}),/*#__PURE__*/_jsx("th",{width:"5%",children:"No"}),/*#__PURE__*/_jsx("th",{width:"35%",children:"Fase Actual"}),/*#__PURE__*/_jsx("th",{width:"50%",children:"Acciones"})]})}),/*#__PURE__*/_jsx(Droppable,{droppableId:"droppable-1",children:function children(provider){return/*#__PURE__*/_jsxs("tbody",_objectSpread(_objectSpread({ref:provider.innerRef},provider.droppableProps),{},{children:[getListaCombinacion.map(function(combinacion,index){return/*#__PURE__*/_jsx(Draggable,{draggableId:index+"",index:index,children:function children(provider){return/*#__PURE__*/_createElement("tr",_objectSpread(_objectSpread({},provider.draggableProps),{},{ref:provider.innerRef,key:index}),/*#__PURE__*/_jsxs("td",_objectSpread(_objectSpread({},provider.dragHandleProps),{},{children:[" ",/*#__PURE__*/_jsx("i",{className:"fas fa-hand-paper"})," "]})),/*#__PURE__*/_jsx("td",{children:index+1}),/*#__PURE__*/_jsx("td",{children:combinacion.nombre_fase}),/*#__PURE__*/_jsx("td",{children:/*#__PURE__*/_jsx("button",{type:"button",title:"Eliminar Combinaci\xF3n",className:"btn btn-sm btn-danger",onClick:function onClick(){return eliminarCombinacion(index);},children:/