import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import Spinner from '../Utils/Spinner';
import DataTable from 'react-data-table-component';

function DepartamentoLista() {
    const [departamentoLista, setDepartamentoLista] = useState({ data: [] });
    const [departamentoListaTotal, setDepartamentoListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Nombre',
            selector: departamento => departamento.attributes.nombre,
            sortable: true,
            width: "50%"
        },
        {
            name: 'CÃ³digo Dane',
            selector: departamento => departamento.attributes.codigo_dane,
            sortable: true,
        },
        {
            name: 'Estado',
            selector: departamento => (departamento.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            cargarDepartamentos(1, 10);
        }
        fetchData();
    }, []);

    const cargarDepartamentos = (page, perPage) => {

        GenericApi.getAllGeneric('departamento/departamento-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setDepartamentoLista(datos);
                    setDepartamentoListaTotal(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal()
                    window.showSpinner(false);
                }
            }
        )
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarDepartamentos(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarDepartamentos(page, newPerPage)

    }


    const cambiarEstado = (objeto, estado) => {

        const data = {
            "data": {
                "type": "mas_departamento",
                "attributes": {
                    "nombre": objeto.attributes.nombre,
                    "codigo_dane": objeto.attributes.codigo_dane,
                    "estado": estado
                }
            }
        }

        GenericApi.updateGeneric('departamento', objeto.id, data).then(
            datos => {
                if (!datos.error) {
                    cargarDepartamentos();
                }
                else
                    window.showModal();
            }
        )

    }

    const departamento = () => {
        return (
            departamentoLista.data.map((departamento, i) => {
                return (
                    <tr key={departamento.id}>
                        <td>{departamento.attributes.nombre}</td>
                        <td>{departamento.attributes.codigo_dane}</td>
                        <td>
                            {departamento.attributes.estado == '1' ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${departamento.id}`}>
                                <button type="button" className="btn btn-primary btn-sm" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>

                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setDepartamentoLista(departamentoListaTotal);
            }
            else {
                var filteredData = {
                    data: departamentoListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + suggestion.attributes.codigo_dane
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()
                            ))
                    )
                };
                setDepartamentoLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de departamentos</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Departamentos registrados</h3>
                    <Link to={`/Departamento/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar departamento</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={departamentoLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                paginationServer
                                noDataComponent="Sin datos"
                                paginationTotalRows={departamentoLista.meta ? departamentoLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por pÃ¡gina' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DepartamentoLista;
