import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import Spinner from '../Utils/Spinner';
import ProcesoDiciplinarioApi from "./../Api/Services/ProcesoDiciplinarioApi";
import ParametrosMasApi from "./../Api/Services/ParametrosMasApi";
import { Navigate } from "react-router-dom";

function ProcesoDisciplinario() {
   
    const [respuestaOrigenRadicado, setRespuestaOrigenRadicado] = useState(false);
    const [respuestaTipoProceso, setRespuestaTipoProceso] = useState(false);
    const [respuestaDependenciaOrigen, setDependenciaOrigen] = useState(false);
    const [listaOrigenRadicado, setListaOrigenRadicado] = useState({ data:{} });
    const [listaTipoProceso, setListaTipoProceso] = useState({ data:{} });
    const [listaDependenciaOrigen, setListaDependenciaOrigen] = useState({ data:{} });
    const [selectedTipoIngreso, setSelectedTipoIngreso] = useState("");
    const [countTextArea, setCountTextArea] = useState(0);
    const [validacionExitosa, setValidacionExitosa] = useState(false);    
    const [radicacionSirius, setRadicacionSirius] = useState({ data: {} });
    const [radicacionDesglose, setRadicacionDesglose] = useState({ data: {} });
    const [radicacionSinproc, setRadicacionSinproc] = useState({ data: {} });
    const [datosFormSiriusConsulta, setDatosFormSiriusConsulta] = useState({});
    const [datosFormDesgloseConsulta, setDatosFormDesgloseConsulta] = useState({});
    const [datosFormSinprocConsulta, setDatosFormSinprocConsulta] = useState({});
    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    useEffect(() => {
        async function fetchData() {
            ParametrosMasApi.getAllTiposOrigenRadicado().then(
                datos => !datos.error ? (setListaOrigenRadicado(datos), setRespuestaOrigenRadicado(true)) : window.showModal(1)
            )
            ParametrosMasApi.getAllTiposProceso().then(
                datos => !datos.error ? (setListaTipoProceso(datos), setRespuestaTipoProceso(true) ) : window.showModal(1)
            )
            ParametrosMasApi.getAllTiposDependenciaOrigen().then(
                datos => !datos.error ? (setListaDependenciaOrigen(datos), setDependenciaOrigen(true) ) : window.showModal(1)
            )            
        }
        fetchData();
    }, []);

    let selectChangeProcesoDisciplinario = (e) => {
        setCountTextArea(0);
        setSelectedTipoIngreso(e);
    }
    
    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to="/MisPendientes" />;
    }

    const consultarRadicado = (datos) => {
        window.showSpinner(true);
        if(selectedTipoIngreso === '1'){//SIRIUS
            setDatosFormSiriusConsulta(datos);
            const data = {
                "data": {
                    "type": "proceso_sirius",
                    "attributes": {
                        "radicado": datos.codigoSirius,
                        "id_origen_radicado": "1",
                        "id_tipo_proceso": 1
                    }
                }
            }
            ProcesoDiciplinarioApi.consultarRadicadoSirius(data).then(
                datos => {
                    if(!datos.error){
                        window.showModal(2)
                        setCountTextArea(datos.data.attributes.antecedente.length)
                        setRadicacionSirius(datos)
                        setValidacionExitosa(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        }
        else if(selectedTipoIngreso === '2'){//DESGLOCE
            setDatosFormDesgloseConsulta(datos);
            const data = {
                "data": {
                    "type": "proceso_desgloce",
                    "attributes": {
                        "radicado": datos.codigoDesglose,
                        "id_origen_radicado": selectedTipoIngreso,
                        "id_tipo_proceso": datos.origenRadicado,
                        "vigencia": datos.vigenciaDesglose,
                        "observacion_mesa_trabajo": datos.mesaTrabajo
                    }
                }
            }
            ProcesoDiciplinarioApi.consultarRadicadoDesgloce(data).then(
                datos => {
                    if(!datos.error){
                        console.log(datos);
                        window.showModal(2)
                        setCountTextArea(datos.data.attributes.antecedente.length)
                        setRadicacionDesglose(datos)
                        setValidacionExitosa(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        }
        else{//SINPROC
            setDatosFormSinprocConsulta(datos);
            const data = {
                "data": {
                    "type": "proceso_sinproc",
                    "attributes": {
                        "radicado": datos.codigoSinproc,
                        "id_origen_radicado": selectedTipoIngreso,
                        "id_tipo_proceso": datos.origenRadicado
                    }
                }
            }
            ProcesoDiciplinarioApi.consultarRadicadoSinproc(data).then(
                datos => {
                    if(!datos.error){
                        console.log(datos);
                        window.showModal(2)
                        setCountTextArea(datos.data.attributes.antecedente.length)
                        setRadicacionSinproc(datos)
                        setValidacionExitosa(true)
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                    window.showSpinner(false);
                }
            )
        }
        
    }

    const enviarDatos = (datos) => {

        let data;

        if(selectedTipoIngreso === '1'){
            data = {
                "data": {
                    "type": "proceso_diciplinario",
                    "attributes": {
                        "radicado": datosFormSiriusConsulta.codigoSirius,
                        "id_origen_radicado": datosFormSiriusConsulta.origenRadicado,
                        "id_tipo_proceso": selectedTipoIngreso,
                        "antecedente": datos.antecedenteSirius,
                        "fecha_ingreso": datos.fechaDependencia,
                        "radicado_entidad": datos.radicadoSirius
                    }
                }
            }
        }
        else if(selectedTipoIngreso === '2'){
            data = {
                "data": {
                    "type": "proceso_diciplinario",
                    "attributes": {
                        "radicado": datosFormDesgloseConsulta.codigoDesglose,
                        "id_origen_radicado": datosFormDesgloseConsulta.origenRadicado,
                        "id_tipo_proceso": selectedTipoIngreso,
                        "antecedente": datos.antecedentesDesglose,
                        "fecha_ingreso": datos.fechaIngresoDesglose,
                        "numero_auto": datos.numAutoDesglose,
                        "auto_asociado": datos.vigenciaAutoDesglose,
                        "fecha_auto_desglose": datos.fechaAutoDesglose,
                        "id_dependencia_origen": datos.dependenciaOrigenDesglose,
                        "observacion_mesa_trabajo": datosFormDesgloseConsulta.mesaTrabajo,
                        "vigencia": datosFormDesgloseConsulta.vigenciaDesglose,
                    }
                }
            }
        }
        else{
            data = {
                "data": {
                    "type": "proceso_diciplinario",
                    "attributes": {
                        "radicado": datosFormSinprocConsulta.codigoSinproc,
                        "id_origen_radicado": datosFormSinprocConsulta.origenRadicado,
                        "id_tipo_proceso": selectedTipoIngreso,
                        "antecedente": datos.antecedenteSinproc,
                        "fecha_ingreso": datos.fechaIngresoSinproc,
                        "vigencia": datos.vigenciaSinproc,
                    }
                }
            }
        }
        console.log(data);
        ProcesoDiciplinarioApi.addProcesoDiciplinario(data).then(
            datos => {
                if(!datos.error){
                    window.showModal(2)
                    setIsRedirect(true);
                }
                else{
                    window.showModal(1)
                }
            }
        )
    }

    /*
    * Componentes
    */

    const selectOrigenRadicado = () => {
        return(
            listaOrigenRadicado.data.map((origen, i) => {
                return (
                    <option key={origen.id} value={origen.id}>{origen.attributes.nombre}</option>
                )
            })
        )
    }

    const selectTipoProceso = () => {
        return (
            listaTipoProceso.data.map((proceso, i) => {
                return (
                    <option key={proceso.id} value={proceso.id}>{proceso.attributes.nombre}</option>
                )
            })
        )
    }

    const selectDependenciaOrigen = () => {
        return (
            listaDependenciaOrigen.data.map((dependencia, i) => {
                return (
                    <option key={dependencia.id} value={dependencia.id}>{dependencia.attributes.nombre}</option>
                )
            })
        )
    }

    const componentSirius = (paso) => {
        if(paso === 1){
            return (
                <>
                    <Formik
                        initialValues={{
                            codigoSirius: '',
                            origenRadicado: ''
                        }}
                        validate={(valores) => {
                            let errores = {}
                            if(!valores.codigoSirius){
                                errores.codigoSirius = 'Debe ingresar un c√≥digo de SIRIUS'
                            }
                            if(!valores.origenRadicado){
                                errores.origenRadicado = 'Debe seleccionar un origen'
                            }
                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            consultarRadicado(valores)
                        }}>
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="codigoSirius">SIRIUS <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" type="text" id="codigoSirius" name="codigoSirius" placeholder="SIRUS" />
                                            <ErrorMessage name="codigoSirius" component={() => (<span className="text-danger">{errors.codigoSirius}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="origenRadicado">Origen del radicado <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="origenRadicado" name="origenRadicado">
                                                <option key="0" value="">Por favor seleccione</option>
                                                { respuestaOrigenRadicado ? selectOrigenRadicado() : null }
                                            </Field>
                                            <ErrorMessage name="origenRadicado" component={() => (<span className="text-danger">{errors.origenRadicado}</span>)} />
                                        </div>
                                    </div>
                                    <div className="block-content block-content-full">
                                        <div className="alert alert-success" role="alert">
                                            <p className="mb-0">Tenga en cuenta que para consular y validar el radicado se debe digitar con el mismo formato generado por SIRIUS:</p>
                                            <p className="mb-0"> * 15 caracteres.</p>
                                            <p className="mb-0"> * Incluir los dos (2) guiones "-".</p>
                                            <p className="mb-0">Ej: 2020-EE-0000002, 2020-ER-0000123</p>
                                        </div>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary">
                                        <i className="fa fa-fw fa-search"></i> Validar
                                    </button>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
        else{
            return (
                <>
                    <Formik
                        initialValues={{ 
                            antecedenteSirius: (radicacionSirius.data?.attributes?.antecedente ? radicacionSirius.data.attributes.antecedente : ''),
                            radicadoSirius: '',
                            fechaRadicadoCordis: (radicacionSirius.data?.attributes?.fecha_cordis ? radicacionSirius.data.attributes.fecha_cordis : ''),
                            fechaDependencia: ''
                        }}
                        enableReinitialize
                        validate={(valores) => {
                            setCountTextArea(valores.antecedenteSirius.length)
                            let errores = {}
                            
                            if(!valores.antecedenteSirius){
                                errores.antecedenteSirius = 'Debe escribir los antecedentes'
                            }
                            else if(valores.antecedenteSirius.length <= 50){
                                errores.antecedenteSirius = 'Los antecedentes deben tener almenos 50 caracteres'
                            }
                            
                            if(!valores.radicadoSirius){
                                errores.radicadoSirius = 'Debe escribir el radicado de la entidad'
                            }

                            if(!valores.fechaRadicadoCordis){
                                errores.fechaRadicadoCordis = 'Debe ingresar la fecha del radicado'
                            }
                            
                            if(!valores.fechaDependencia){
                                errores.fechaDependencia = 'Debe ingresar la fecha de ingreso de la dependencia'
                            }

                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            enviarDatos(valores)
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-12">                                        
                                        <div className="text-center">
                                            <label>RADICADO No.: <span className="text-danger">{datosFormSiriusConsulta.codigoSirius}</span></label>
                                        </div>
                                    </div>
                                    <div className="col-md-12">                                        
                                        <div className="form-group">
                                            <label htmlFor="antecedenteSirius">Antecedentes <span className="text-danger">*</span></label>
                                            <Field as="textarea" className="form-control" id="antecedenteSirius" name="antecedenteSirius" rows="6" placeholder="Antecedentes...." maxLength="4000"></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / 4000</span>                                    
                                            </div>
                                            <ErrorMessage name="antecedenteSirius" component={() => (<span className="text-danger">{errors.antecedenteSirius}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor="radicadoSirius">No. Radicado Entidad <span className="text-danger">*</span></label>
                                            <Field type="text" id="radicadoSirius" name="radicadoSirius" className="form-control" placeholder="No. Radicado Entidad"/>
                                            <ErrorMessage name="radicadoSirius" component={() => (<span className="text-danger">{errors.radicadoSirius}</span>)} />
                                        </div>  
                                    </div>
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor='fechaRadicadoCordis'>Fecha de radicado CORDIS <span className="text-danger">*</span></label>
                                            <Field type="date" id="fechaRadicadoCordis" name="fechaRadicadoCordis" className="form-control" disabled/>
                                            <ErrorMessage name="fechaRadicadoCordis" component={() => (<span className="text-danger">{errors.fechaRadicadoCordis}</span>)} />
                                        </div>  
                                    </div>                                    
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor='fechaDependencia'>Fecha de ingreso a dependencia <span className="text-danger">*</span></label>
                                            <Field type="date" id="fechaDependencia" name="fechaDependencia" className="form-control" />
                                            <ErrorMessage name="fechaDependencia" component={() => (<span className="text-danger">{errors.fechaDependencia}</span>)} />
                                        </div>  
                                    </div>
                                    
                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fa fa-fw fa-save"></i> Guardar informaci√≥n
                                        </button>
                                    </div>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }        
    };

    const componentDesglose = (paso) => {
        if(paso === 1){
            return (        
                <>
                    <Formik
                        initialValues={{ 
                            codigoDesglose: '',
                            vigenciaDesglose: '',
                            origenRadicado: '',
                            mesaTrabajo: ''
                        }}
                        validate={(valores) => {
                            let errores = {}
                            if(!valores.codigoDesglose){
                                errores.codigoDesglose = 'Debe ingresar un c√≥digo de Desglose'
                            }

                            if(!valores.vigenciaDesglose){
                                errores.vigenciaDesglose = 'Debe ingresar la vigencia'
                            }

                            if(!valores.origenRadicado){
                                errores.origenRadicado = 'Debe seleccionar un origen'
                            }

                            if(!valores.mesaTrabajo){
                                errores.mesaTrabajo = 'Debe escribir la mesa de trabajo'
                            }
                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            consultarRadicado(valores)
                        }}>
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor="codigoDesglose">No. SINPROC <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" type="text" id="codigoDesglose" name="codigoDesglose" placeholder="Desglose" />
                                            <ErrorMessage name="codigoDesglose" component={() => (<span className="text-danger">{errors.codigoDesglose}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor="vigenciaDesglose">Vigencia <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" type="number" id="vigenciaDesglose" name="vigenciaDesglose" placeholder="Vigencia" />
                                            <ErrorMessage name="vigenciaDesglose" component={() => (<span className="text-danger">{errors.vigenciaDesglose}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label htmlFor="origenRadicado">Origen del radicado <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="origenRadicado" name="origenRadicado">
                                                <option value="">Por favor seleccione</option>                                                
                                                { respuestaOrigenRadicado ? selectOrigenRadicado() : null }
                                            </Field>
                                            <ErrorMessage name="origenRadicado" component={() => (<span className="text-danger">{errors.origenRadicado}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="mesaTrabajo">Observaci√≥n de mesa de trabajo <span className="text-danger">*</span></label>
                                            <Field as="textarea" className="form-control" id="mesaTrabajo" name="mesaTrabajo" rows="6" placeholder="Mesa de trabajo...." maxLength="4000"></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / 4000</span>                                    
                                            </div>
                                            <ErrorMessage name="mesaTrabajo" component={() => (<span className="text-danger">{errors.mesaTrabajo}</span>)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary">
                                        <i className="fa fa-fw fa-search"></i> Validar
                                    </button>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
        else{
            return (        
                <>
                    <Formik
                        initialValues={{
                            antecedentesDesglose: (radicacionDesglose.data?.attributes?.antecedente ? radicacionDesglose.data.attributes.antecedente : ''),
                            fechaIngresoDesglose: '',
                            vigenciaAutoDesglose: '',
                            fechaAutoDesglose: '',
                            numAutoDesglose: '',
                            dependenciaOrigenDesglose: ''
                        }}
                        enableReinitialize
                        validate={(valores) => {
                            setCountTextArea(valores.antecedentesDesglose.length)
                            let errores = {}
                            
                            if(!valores.antecedentesDesglose){
                                errores.antecedentesDesglose = 'Debe escribir los antecedentes'
                            }
                            else if(valores.antecedentesDesglose.length <= 50){
                                errores.antecedentesDesglose = 'Los antecedentes deben tener almenos 50 caracteres'
                            }
                            
                            if(!valores.fechaIngresoDesglose){
                                errores.fechaIngresoDesglose = 'Debe ingresar la fecha de ingreso'
                            }

                            if(!valores.vigenciaAutoDesglose){
                                errores.vigenciaAutoDesglose = 'Debe ingresar la vigencia de auto'
                            }
                            
                            if(!valores.fechaAutoDesglose){
                                errores.fechaAutoDesglose = 'Debe ingresar la fecha de auto'
                            }

                            if(!valores.numAutoDesglose){
                                errores.numAutoDesglose = 'Debe ingresar el numero de auto'
                            }
                            else if(valores.numAutoDesglose.length < 4){
                                errores.numAutoDesglose = 'Debe ingresar almenos 4 caracteres del numero de auto'
                            }
                            else if(valores.numAutoDesglose.length > 11){
                                errores.numAutoDesglose = 'Debe ingresar menos de 11 caracteres del numero de auto'
                            }

                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            enviarDatos(valores)
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-12">                                        
                                        <div className="text-center">
                                            <label>SINPROC No.: {datosFormDesgloseConsulta.codigoDesglose}<span className="text-danger"></span></label>
                                            <div className="block-content block-content-full">
                                                <div className="alert alert-success" role="alert">
                                                    <p className="mb-0">{ radicacionDesglose.data?.attributes?.antecedente }</p>
                                                </div>
                                            </div>
                                            {/*<label>Atenci√≥n: El n√∫mero SINPROC Ingresado [{datosFormSinprocConsulta.codigoSinproc}] No tiene asociado desgloses previos</label>*/}
                                        </div>
                                    </div>
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="antecedentesDesglose">Antecedentes <span className="text-danger">*</span></label>
                                            <Field as="textarea" className="form-control" id="antecedentesDesglose" name="antecedentesDesglose" rows="6" placeholder="Antecedentes...." maxLength="4000"></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / 4000</span>                                    
                                            </div>
                                            <ErrorMessage name="antecedentesDesglose" component={() => (<span className="text-danger">{errors.antecedentesDesglose}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor='fechaIngresoDesglose'>Fecha de ingreso a dependencia <span className="text-danger">*</span></label>
                                            <Field type="date" id="fechaIngresoDesglose" name="fechaIngresoDesglose" className="form-control"/>
                                            <ErrorMessage name="fechaIngresoDesglose" component={() => (<span className="text-danger">{errors.fechaIngresoDesglose}</span>)} />
                                        </div>  
                                    </div>                                
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor='vigenciaAutoDesglose'>Vigencia del auto <span className="text-danger">*</span></label>
                                            <Field type="text" id="vigenciaAutoDesglose" name="vigenciaAutoDesglose" className="form-control" placeholder="Vigencia" />
                                            <ErrorMessage name="vigenciaAutoDesglose" component={() => (<span className="text-danger">{errors.vigenciaAutoDesglose}</span>)} />
                                        </div>  
                                    </div>
                                    <div className="col-md-4">                                        
                                        <div className="form-group">
                                            <label htmlFor='fechaAutoDesglose'>Fecha del auto de desglose <span className="text-danger">*</span></label>
                                            <Field type="date" id="fechaAutoDesglose" name="fechaAutoDesglose" className="form-control"/>
                                            <ErrorMessage name="fechaAutoDesglose" component={() => (<span className="text-danger">{errors.fechaAutoDesglose}</span>)} />
                                        </div>  
                                    </div>
                                    <div className="col-md-6">                                        
                                        <div className="form-group">
                                            <label htmlFor='numAutoDesglose'>No. del auto <span className="text-danger">*</span></label>
                                            <Field type="text" id="numAutoDesglose" name="numAutoDesglose" minLength="4" maxLength="8" className="form-control"/>
                                            <ErrorMessage name="numAutoDesglose" component={() => (<span className="text-danger">{errors.numAutoDesglose}</span>)} />
                                        </div>  
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="dependenciaOrigenDesglose">Dependencia origen <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="dependenciaOrigenDesglose" name="dependenciaOrigenDesglose">
                                                <option value="">Por favor seleccione</option>
                                                { respuestaDependenciaOrigen ? selectDependenciaOrigen() : null }
                                            </Field>
                                            <ErrorMessage name="dependenciaOrigenDesglose" component={() => (<span className="text-danger">{errors.dependenciaOrigenDesglose}</span>)} />
                                        </div>
                                    </div>
                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fa fa-fw fa-save"></i> Guardar informaci√≥n
                                        </button>
                                    </div>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
    };

    const componentSinproc = (paso) => {
        if(paso === 1){
            return (
                <>
                <Formik
                        initialValues={{ 
                            codigoSinproc: '',
                            origenRadicado: ''
                        }}
                        validate={(valores) => {
                            let errores = {}
                            if(!valores.codigoSinproc){
                                errores.codigoSinproc = 'Debe ingresar un c√≥digo de SIRIUS'
                            }                        
                            if(!valores.origenRadicado){
                                errores.origenRadicado = 'Debe seleccionar un origen'
                            }
                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            consultarRadicado(valores)
                        }}>
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="codigoSinproc">No SINPROC <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" type="text" id="codigoSinproc" name="codigoSinproc" placeholder="SINPROC" />
                                            <ErrorMessage name="codigoSinproc" component={() => (<span className="text-danger">{errors.codigoSinproc}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="origenRadicado">Origen del radicado <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="origenRadicado" name="origenRadicado">
                                                <option value="">Por favor seleccione</option>                                                
                                                { respuestaOrigenRadicado ? selectOrigenRadicado() : null }
                                            </Field>
                                            <ErrorMessage name="origenRadicado" component={() => (<span className="text-danger">{errors.origenRadicado}</span>)} />
                                        </div>
                                    </div>
                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fa fa-fw fa-search"></i> Validar
                                        </button>
                                    </div>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
        else{
            return (
                <>
                    <Formik
                        initialValues={{
                            antecedenteSinproc: (radicacionSinproc.data?.attributes?.antecedente ? radicacionSinproc.data.attributes.antecedente : ''),
                            fechaIngresoSinproc: '',
                            vigenciaSinproc: ''
                        }}
                        enableReinitialize
                        validate={(valores) => {
                            setCountTextArea(valores.antecedenteSinproc.length)
                            let errores = {}
                            
                            if(!valores.antecedenteSinproc){
                                errores.antecedenteSinproc = 'Debe escribir los antecedentes'
                            }
                            else if(valores.antecedenteSinproc.length <= 50){
                                errores.antecedenteSinproc = 'Los antecedentes deben tener almenos 50 caracteres'
                            }
                            
                            if(!valores.fechaIngresoSinproc){
                                errores.fechaIngresoSinproc = 'Debe ingresar la fecha de ingreso a dependencia'
                            }

                            if(!valores.vigenciaSinproc){
                                errores.vigenciaSinproc = 'Debe ingresar la vigencia'
                            }                            

                            return errores
                        }}
                        onSubmit={(valores, {resetForm}) => {
                            enviarDatos(valores)
                            //resetForm();
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="row">
                                    <div className="col-md-12">                                        
                                        <div className="text-center">
                                            <label>SINPROC No.: <span className="text-danger">{datosFormSinprocConsulta.codigoSinproc}</span></label>
                                        </div>
                                    </div>
                                    <div className="col-md-12">                                        
                                        <div className="form-group">
                                            <label htmlFor="antecedenteSinproc">Antecedentes <span className="text-danger">*</span></label>
                                            <Field as="textarea" className="form-control" id="antecedenteSinproc" name="antecedenteSinproc" rows="6" placeholder="Antecedentes...." maxLength="4000"></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / 4000</span>                                    
                                            </div>
                                            <ErrorMessage name="antecedenteSinproc" component={() => (<span className="text-danger">{errors.antecedenteSinproc}</span>)} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">                                        
                                        <div className="form-group">
                                            <label htmlFor='fechaIngresoSinproc'>Fecha de ingreso a dependencia <span className="text-danger">*</span></label>
                                            <Field type="date" id="fechaIngresoSinproc" name="fechaIngresoSinproc" className="form-control"/>
                                            <ErrorMessage name="fechaIngresoSinproc" component={() => (<span className="text-danger">{errors.fechaIngresoSinproc}</span>)} />
                                        </div>  
                                    </div>                                
                                    <div className="col-md-6">                                        
                                        <div className="form-group">
                                            <label htmlFor='vigenciaSinproc'>Vigencia <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="vigenciaSinproc" name="vigenciaSinproc">
                                                <option value="">Por favor seleccione</option>
                                                <option value="2021">2021</option>
                                                <option value="2020">2020</option>
                                                <option value="2019">2019</option>
                                            </Field>
                                            <ErrorMessage name="vigenciaSinproc" component={() => (<span className="text-danger">{errors.vigenciaSinproc}</span>)} />

                                        </div>  
                                    </div>
                                    
                                    <div className="block-content block-content-full text-right">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fa fa-fw fa-save"></i> Guardar informaci√≥n
                                        </button>
                                    </div>
                                </div>
                            </Form>
                        )}
                    </Formik>
                </>
            );
        }
    };

    return(
        <>
            { isRedirect ? redirectToRoutes() : null }
            <Spinner />
            { <InfoErrorApi error={errorApi}/> }
            { <InfoExitoApi /> }
            <div className="content">
                <div className="row">
                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Paso 1: Validar forma de ingreso</h3>
                            </div>
                            <div className="block-content">                         
                                <div className="row">
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="ingresoProcesoDisciplinario">Ingreso por <span className="text-danger">*</span></label>
                                            <select className="form-control" id="ingresoProcesoDisciplinario" name="ingresoProcesoDisciplinario" value={selectedTipoIngreso} 
                                            onChange={e => selectChangeProcesoDisciplinario(e.target.value)}>
                                                <option value="">Por favor seleccione</option>
                                                { respuestaTipoProceso ? selectTipoProceso() : null }
                                            </select>
                                        </div>
                                    </div>                       
                                </div>
                                    {/*SIRIUS*/}
                                    { selectedTipoIngreso === '1' ? componentSirius(1) : '' }
                                    {/*DESGLOSE*/}
                                    { selectedTipoIngreso === '2' ? componentDesglose(1) : '' }
                                    {/*SINPROC*/}
                                    { selectedTipoIngreso === '3' ? componentSinproc(1) : '' }    
                            </div>
                        </div>
                    </div>
                    {
                        validacionExitosa ?
                            <div className="col-md-12"> 
                                <div className="block block-themed">
                                    <div className="block-header">
                                        <h3 className="block-title">Paso 2: Registro de la informaci√≥n de ingreso</h3>
                                    </div>
                                    <div className="block-content">                                
                                        {/*SIRIUS*/}       
                                        { selectedTipoIngreso === '1' ? componentSirius(2) : null }
                                        {/*DESGLOSE*/}
                                        { selectedTipoIngreso === '2' ? componentDesglose(2) : null }
                                        {/*SINPROC*/}
                                        { selectedTipoIngreso === '3' ? componentSinproc(2) : null }
                                    </div>
                                </div>
                            </div>
                        : null
                    }
                </div>
            </div>
        </>
    )

}

export default ProcesoDisciplinario;
