import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

function EvaluacionFasesDetalle() {

    const [getListaFases, setListaFases] = useState([]);
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getNombreEvaluacion, setNombreEvaluacion] = useState({ data: [], links: [], meta: [] });
    const [getNombreExpediente, setNombreExpediente] = useState({ data: [], links: [], meta: [] });
    const [getNombreSubExpediente, setNombreSubExpediente] = useState({ data: [], links: [], meta: [] });
    const [getExisteEvaluacion, setExisteEvaluacion] = useState(false);

    let { id_tipo_expediente, id_sub_tipo_expediente, id_tipo_evaluacion } = useParams();

    useEffect(() => {
        async function fetchData() {

            getListaFasesPorTipoExpediente();

          
        }
        fetchData();
    }, []);

    const getListaFasesPorTipoExpediente = () => {
        window.showSpinner(true);
        GenericApi.getGeneric("administracion/evaluacion/lista-expedientes-by-id-tipo-expediente/"+id_tipo_expediente+"/"+id_sub_tipo_expediente+"/"+id_tipo_evaluacion).then(
            datos => {
                if (!datos.error) {
                    setListaFases(datos["data"]);
                    getNombreTipoEvaluacion();
                }
                else {
                    setModalState({ title: "SINPROC No ", message: datos.error.toString(), show: true, redirect: '/ClasificacionRadicadoLista', alert: global.Constants.TIPO_ALERTA.ERROR }); 
                    window.showSpinner(false);
                }
                
            }
        )
    }


    const getNombreTipoEvaluacion = () => {

        GenericApi.getGeneric("administracion/nombre-evaluacion/"+id_tipo_evaluacion).then(
            datos => {
                if (!datos.error) {
                    setNombreEvaluacion(datos.data[0].attributes.nombre);
                    getNombreTipoExpediente();                    
                }
                else {
                    setModalState({ title: "SINPROC No ", message: datos.error.toString(), show: true, redirect: '/ClasificacionRadicadoLista', alert: global.Constants.TIPO_ALERTA.ERROR }); 
                    window.showSpinner(false);
                }
            }
        )
    }

    const getNombreTipoExpediente = () => {

        GenericApi.getGeneric("administracion/nombre-expediente/"+id_tipo_expediente).then(
            datos => {
                if (!datos.error) {
                    setNombreExpediente(datos.data[0].attributes.nombre);
                    getNombreTipoSubExpediente()
                }
                else {
                    setModalState({ title: "SINPROC No ", message: datos.error.toString(), show: true, redirect: '/ClasificacionRadicadoLista', alert: global.Constants.TIPO_ALERTA.ERROR }); 
                    window.showSpinner(false);
                }
                
            }
        )
    }

    const getNombreTipoSubExpediente = () => {

        GenericApi.getGeneric("administracion/nombre-sub-tipo-expediente/"+id_tipo_expediente+"/"+id_sub_tipo_expediente).then(
            datos => {
                if (!datos.error) {
                    setNombreSubExpediente(datos.data[0].attributes.nombre);
                    setExisteEvaluacion(true);
                }
                else {
                    setModalState({ title: "SINPROC No ", message: datos.error.toString(), show: true, redirect: '/ClasificacionRadicadoLista', alert: global.Constants.TIPO_ALERTA.ERROR }); 
                }
                window.showSpinner(false);
            }
        )
    }


    const handleDragEnd = (e) => {
        if (!e.destination) return;
        let tempData = Array.from(getListaFases);
        let [source_data] = tempData.splice(e.source.index, 1);
        tempData.splice(e.destination.index, 0, source_data);
        setListaFases(tempData);
    };

    const eliminarFase = (index) => {
        setListaFases(getListaFases.filter((img, i) => i !== index));
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "",
                    "update_at": Date.now(),
                    "estado_observacion": datos.descripcion,
                    "estado": global.Constants.ESTADOS.ACTIVO,
                }
            }
        }

       GenericApi.updateGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No  :: Actualizar antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista',  alert: global.Constants.TIPO_ALERTA.EXITO});
                    window.showSpinner(false);

                }
                else {
                    setModalState({ title: "SINPROC No  :: Actualizar antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista',  alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showSpinner(false);
                    
                }
            }
        )

    }

    return (
        <>
            {<Spinner/>}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                  
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                       <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/EvaluacionFasesLista/`}><small>Configuración de fases etapa evaluación</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Lista de fases</small></li>
                                </ol>
                            </nav>
                        </div> 

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Configuración de fases en Etapa de Evaluación</h3>
                            </div>

                            <div className="block-content">
    
                                <div className='row'>

                                    <div className="col-md-12 alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4">Alerta</h3>
                                        <p className="mb-0">Por defecto fase de validar clasificado y evaluación de la Etapa Evaluación son asignadas por defecto.</p>
                                    </div>
                                </div>

                                <div className='row'>

                                    {getExisteEvaluacion?
                                    <div className='col-md-12'>
                                        <span><strong>Tipo de Expediente:</strong> {getNombreExpediente} {getNombreSubExpediente} </span> 
                                        <span><strong>Tipo de Evaluación:</strong> {getNombreEvaluacion}</span>
                                    </div>
                                    
                                    :null}
                                </div>

                                <div className='row'>
                                    <div className='col-md-12'>
                                        <div className="form-group ">

                                        <DragDropContext onDragEnd={handleDragEnd}>

                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        <th width="10%">Mover</th>
                                                        <th width="10%">No</th>
                                                        <th width="70%">Fase Actual</th>
                                                        <th width="10%">Acciones</th>
                                                    </tr>
                                                </thead>

                                                <Droppable droppableId="droppable-1">
                                                   {(provider) => (
                                                        <tbody className="text-capitalize" ref={provider.innerRef} {...provider.droppableProps}>

                                                            {getListaFases.map((linea, index) => (

                                                                <Draggable key={index} draggableId={index+""} index={index}>
                                                                    {(provider) => (
                                                                    <tr {...provider.draggableProps} ref={provider.innerRef} key={index}>
                                                                        <td  {...provider.dragHandleProps}> <i className="fas fa-hand-paper"></i> </td>
                                                                        <td>{ index + 1 }</td>
                                                                        <td>{ linea.attributes.nombre_fase_actual}</td>
                                                                        <td>
                                                                            <button type='button' title='Eliminar Rol' className='btn btn-sm btn-danger' onClick={() => eliminarFase(index)}><i className="fas fa-minus-circle"></i></button>
                                                                        </td>
                                                                    </tr>
                                                                )}
                                                                </Draggable>
                                                            
                                                            ))}
                                                            
                                                        </tbody>
                                                     )}
                                                </Droppable>

                                            </table>
                                        </DragDropContext>
                                            

                                        </div>
                                    </div>
                                </div>

                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default EvaluacionFasesDetalle;
