import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import ClasificacionRadicadoApi from "../../Api/Services/ClasificacionRadicadoApi";
import EntidadInvestigadoApi from "../../Api/Services/EntidadInvestigadoApi";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Navigate } from "react-router-dom";
import Autocomplete from "../../Autocomplete/Autocomplete";
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
function EntidadInvestigadoForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [selectedRegistro, setSelectedRegistro] = useState("");
    const [idEntidad, setIdEntidad] = useState('');

    const [nombreInvestigado, setNombreInvestigado] = useState('');
    const [cargoInvestigado, setCargoInvestigado] = useState('');
    const [observaciones, setObservaciones] = useState('');
    const [esAnonimo, setEsAnonimo] = useState(false);

    const formatOnlyLetters = /^[A-Za-z]+$/;
    const formatOnlyLettersOrnUmbers = /^[0-9a-zA-Z]+$/;
  

    const [countTextArea, setCountTextArea] = useState(0);
    const [listaEntidad, setListaEntidad] = useState({ data: {} });
    const [respuestaEntidad, setRespuestaEntidad] = useState(false);

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    const redirectToRoutes = () => {
        return <Navigate to={`/EntidadInvestigadoLista/`} state={{ from: from }} />;
    }



    useEffect(() => {

        async function fetchData() {


            ParametrosMasApi.getAllEntidades().then(
                datos => !datos.error ? (setListaEntidad(datos), setRespuestaEntidad(true)) : window.showModal(1)
            )

            obtenerParametros();
        }

        fetchData();
    }, []);

    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const handleCallback = (childData) => {
        try {
            setIdEntidad(childData)
            if (childData) {
                if (childData == '1') {
                    asignarAnonimo();
                }
                else {
                    setEsAnonimo(false);
                    setNombreInvestigado("");
                    setCargoInvestigado("");
                    setObservaciones("");
                }
            }

        } catch (error) {

        }

    }

    let selectChangeRegistro = (e) => {
        setSelectedRegistro(e);

        asignarAnonimoCheck(false);
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        //TIPO EXPEDIENTE => DERECHO DE PETICION => COPIAS
        if (selectedRegistro === "true") {

            data = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_entidad": idEntidad,
                        "nombre_investigado": datos.nombreInvestigado ? datos.nombreInvestigado : nombreInvestigado,
                        "cargo": datos.cargoInvestigado ? datos.cargoInvestigado : cargoInvestigado,
                        "codigo": datos.codigoEntidadInvestigado,
                        "observaciones": datos.observaciones ? datos.observaciones : observaciones,
                        "estado": true,
                        "requiere_registro": true

                    }
                }
            }
        }
        else {
            data = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "id_entidad": null,
                        "nombre_investigado": null,
                        "cargo": null,
                        "codigo": null,
                        "observaciones": null,
                        "estado": true,
                        "requiere_registro": false

                    }
                }
            }
        }

        console.log(JSON.stringify(data));

        EntidadInvestigadoApi.addEntidadInvestigado(data).then(
            datos => {
                if (!datos.error) {
                    from.debeConsultar = true;
                    window.showModal(2)
                    setIsRedirect(true)
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }

    let asignarAnonimo = () => {
        setEsAnonimo(true);
        setNombreInvestigado("NO_APLICA");
        setCargoInvestigado("NO_APLICA");
        setObservaciones("NO_APLICA");

    }


    const handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if (name == "nombreInvestigado") {
            if (value == '')
                setNombreInvestigado('');

            if (formatOnlyLetters.test(value)) {
                setNombreInvestigado(value);
                return true;
            }
            else {
                return false;
            }
        }

        if (name == "cargoInvestigado") {
            if (value == '')
                setCargoInvestigado('');

            if (formatOnlyLetters.test(value)) {
                setCargoInvestigado(value);
                return true;
            }
            else {
                return false;
            }
        }

        if (name == "observaciones") {
            setCountTextArea(value.length)
            setObservaciones(value);
        }





    }


    let asignarAnonimoCheck = (e) => {
        setEsAnonimo(e);

        if (e) {
            setNombreInvestigado("NO_APLICA");
            setCargoInvestigado("NO_APLICA");
            setCountTextArea("NO_APLICA".length)
            setObservaciones("NO_APLICA");
        }
        else {
            setNombreInvestigado("");
            setCargoInvestigado("");
            setCountTextArea("");
            setObservaciones("");
        }

    }

    // COMPOENENTE TIPO EXPEDIENTE
    const componenteEntidadInvestigado = (aplica_entidad) => {

        if (aplica_entidad === true) {
            return (

                <>
                    {<Spinner />}
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    {isRedirect ? redirectToRoutes() : null}


                    <Formik
                        initialValues={{
                            idEntidad: '',
                            codigoEntidadInvestigado: '',
                            nombreInvestigado: '',
                            cargoInvestigado: '',
                            observaciones: '',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            setCountTextArea(observaciones.length)


                            
                            if (!valores.idEntidad && idEntidad == '') {
                                errores.idEntidad = 'Debe seleccionar una entidad'
                            }
                            if (!valores.nombreInvestigado && !nombreInvestigado) {
                                errores.nombreInvestigado = 'Debe agregar un nombre del investigado'
                            }
                            if (!valores.cargoInvestigado && !cargoInvestigado) {
                                errores.cargoInvestigado = 'Debe agregar un cargo'
                            }
                            if (!valores.observaciones && !observaciones) {
                                errores.observaciones = 'Debe agregar observaciones'
                            }
                            else if (observaciones.length <= getMinimoTextArea && observaciones != 'NO_APLICA') {
                                errores.observaciones = 'Las observaciones deben tener almenos ' + getMinimoTextArea + ' caracteres'
                            }
                            //console.log(errores);
                            return errores
                        }}

                        onSubmit={(valores, { resetForm }) => {
                            //console.log (valores);
                            enviarDatos(valores);
                        }}
                    >
                        {({ errors }) => (
                            <Form>
                                <div className="row">

                                    <div className="block block-themed">

                                        <div className="block-content">
                                            <div className="row">
                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label htmlFor="idEntidad">Entidad del investigado <span className="text-danger">*</span></label>
                                                        <Autocomplete suggestions={listaEntidad} parentCallback={handleCallback} />
                                                        <ErrorMessage name="idEntidad" component={() => (<span className="text-danger">{errors.idEntidad}</span>)} />
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row">

                                                <div className="block-content">
                                                    <>
                                                        {
                                                            (esAnonimo == true) ? (
                                                                <div className='text-center'>
                                                                    <div className="custom-control custom-switch custom-control-lg mb-2">
                                                                        <input type="checkbox" checked onChange={e => asignarAnonimoCheck(e.target.checked)} className="custom-control-input" id="example-sw-custom-lg11" name="example-sw-custom-lg11" />
                                                                        <label className="custom-control-label" htmlFor="example-sw-custom-lg11">No conozco los datos</label>
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }

                                                        {
                                                            (esAnonimo == false) ? (
                                                                <div className='text-center'>
                                                                    <div className="custom-control custom-switch custom-control-lg mb-2">
                                                                        <input type="checkbox" onChange={e => asignarAnonimoCheck(e.target.checked)} className="custom-control-input" id="example-sw-custom-lg12" name="example-sw-custom-lg12" />
                                                                        <label className="custom-control-label" htmlFor="example-sw-custom-lg12">No conozco los datos</label>
                                                                    </div>
                                                                </div>
                                                            ) : null
                                                        }
                                                    </>
                                                </div>

                                                <div className="col-md-6">
                                                    <div className="form-group">
                                                        <label htmlFor="nombreInvestigado">Nombre del Investigado <span className="text-danger">*</span></label>
                                                        <Field disabled={esAnonimo} onChange={handleInputChange} as="input" value={nombreInvestigado} className="form-control" type="text" id="nombreInvestigado" name="nombreInvestigado" placeholder="Nombre del Investigado" />
                                                        <ErrorMessage name="nombreInvestigado" component={() => (<span className="text-danger">{errors.nombreInvestigado}</span>)} />
                                                    </div>
                                                </div>

                                                <div className="col-md-6">
                                                    <div className="form-group">
                                                        <label htmlFor="cargoInvestigado">Cargo del Investigado <span className="text-danger">*</span></label>
                                                        <Field disabled={esAnonimo} onChange={handleInputChange} as="input" value={cargoInvestigado} className="form-control" type="text" id="cargoInvestigado" name="cargoInvestigado" placeholder="Cargo del Investigado" />
                                                        <ErrorMessage name="cargoInvestigado" component={() => (<span className="text-danger">{errors.cargoInvestigado}</span>)} />
                                                    </div>
                                                </div>


                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label htmlFor="observaciones">Observaciones <span className="text-danger">*</span></label>
                                                        <Field disabled={esAnonimo} as="textarea" value={observaciones} onChange={handleInputChange} className="form-control" id="observaciones" name="observaciones" rows="4"
                                                            placeholder="Observaciones" maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                                        <div className="text-right">
                                                            <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                                        </div>
                                                        <ErrorMessage name="observaciones" component={() => (<span className="text-danger">{errors.observaciones}</span>)} />
                                                    </div>
                                                </div>

                                                <div className="col-md-12">
                                                    <div className="block-content block-content-full text-right">
                                                        <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                                        <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                                            <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                                        </Link>
                                                    </div>
                                                </div>

                                            </div>
                                        </div>
                                    </div>

                                </div>

                            </Form>
                        )}
                    </Formik>
                </>
            );
        }

        else {

            return (
                <>{isRedirect ? redirectToRoutes() : null}
                    {<Spinner />}
                    {<InfoErrorApi error={errorApi} />}
                    {<InfoExitoApi />}
                    <Formik
                        initialValues={{

                        }}
                        enableReinitialize

                        onSubmit={(valores, { resetForm }) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="row">

                                <div className="block-content">
                                    <div className="row">

                                        <div className="col-md-12">
                                            <div className="block-content block-content-full text-right">
                                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                                <Link to={`/EntidadInvestigadoLista/`} state={{ from: from }}>
                                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                                </Link>
                                            </div>
                                        </div>

                                    </div>
                                </div>
                            </div>

                        </Form>

                    </Formik>
                </>
            );



        }
    };

    // COMPONENTE PRINCIPAL
    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="content">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/EntidadInvestigadoLista/`} state={{ from: from }}><small>Entidad del investigado</small></Link></li>
                                    <li class="breadcrumb-item"><small>Nueva entidad del investigado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="row">


                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Entidad del investigado SINPROC No. {radicado}:: <strong>Crear entidad del investigado</strong></h3>
                            </div>
                            <div className="block-content">

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">Por la Clasificación del Expediente, Seleccione <strong>SI APLICA o NO APLICA</strong> el registro de esta información</p>
                                    </div>

                                    <label htmlFor="require">Aplica registro<span className="text-danger">*</span></label>
                                    <select className="form-control" id="registroExpediente" name="registroExpediente" value={selectedRegistro}
                                        onChange={e => selectChangeRegistro(e.target.value)}>
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </select>

                                    {/*DERECHO DE PETICION*/}
                                    {selectedRegistro === 'true' ? componenteEntidadInvestigado(true) : ''}
                                    {/*PODER REFERENTE A SOLIICTUD*/}
                                    {selectedRegistro === 'false' ? componenteEntidadInvestigado(false) : ''}

                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );

}
export default EntidadInvestigadoForm;
